import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the sidebar and the Settings button
    await page.waitForSelector('#settings-btn', { state: 'visible', timeout: 5000 });

    // Scroll sidebar into view if needed
    const settingsBtn = await page.$('#settings-btn');
    if (settingsBtn) {
      await settingsBtn.scrollIntoViewIfNeeded();
    }

    // Take screenshot before opening modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_settings_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_settings_click.png'));

    // Click the Settings button (by ID)
    await page.click('#settings-btn');

    // Wait for the settings modal to appear (by ID and .modal.active)
    await page.waitForSelector('#settings-modal.modal.active', { timeout: 5000 });

    // Wait for modal header text to be visible
    await page.waitForSelector('#settings-modal .modal-header h3', { state: 'visible', timeout: 3000 });

    // Take screenshot after modal is open
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_settings_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_settings_modal_open.png'));

    // Extract some data from the modal to verify it's open
    const modalTitle = await page.$eval('#settings-modal .modal-header h3', el => el.textContent.trim());
    const tabTexts = await page.$$eval('#settings-modal .settings-tabs .tab-btn', els => els.map(e => e.textContent.trim()));
    const saveBtnVisible = await page.$eval('#save-settings', el => !!el && (el.offsetParent !== null));

    return {
      success: true,
      action: "Opened the Settings modal by clicking the sidebar Settings button and verified modal content.",
      extractedData: {
        modalTitle,
        tabs: tabTexts,
        saveBtnVisible
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

