    // Wait for the sidebar to be visible (using nav.sidebar)
    await page.waitForSelector('nav.sidebar', { state: 'visible' });

    // Use a different selector strategy: Find the "Settings" nav item by its icon and text
    // We use XPath to select the nav-item containing a cog icon and the text "Settings"
    const [settingsNavItem] = await page.$x(
      "//nav[contains(@class, 'sidebar')]//li[contains(@class, 'nav-item')][.//i[contains(@class, 'fa-cog')] and .//span[normalize-space(text())='Settings']]"
    );
    if (!settingsNavItem) {
      throw new Error('Settings nav item not found');
    }

    // Scroll into view and click
    await settingsNavItem.scrollIntoViewIfNeeded();
    await settingsNavItem.click();

    // Wait for the settings modal to appear (using #settings-modal and .modal.active)
    await page.waitForSelector('#settings-modal.modal.active', { timeout: 3000 });

    // Wait for the modal header to contain "Settings"
    await page.waitForSelector("#settings-modal .modal-header h3", { state: 'visible' });
    const modalTitle = await page.textContent("#settings-modal .modal-header h3");

    // Take screenshot after modal opens
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_settings_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_settings_modal_open.png'));

    // Extract some data from the modal for verification
    // For example, get the visible tab name and the username field value
    const activeTab = await page.textContent(
      "#settings-modal .settings-tabs .tab-btn.active"
    );
    const usernameValue = await page.inputValue('#settings-username');

    return {
      success: true,
      action: "Opened the Settings modal by clicking the sidebar Settings nav item. Verified modal title and extracted username.",
      extractedData: {
        modalTitle: modalTitle && modalTitle.trim(),
        activeTab: activeTab && activeTab.trim(),
        usernameValue
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const initialShot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialShot });
    screenshots.push(initialShot);

    // Wait for the sidebar to be visible (using nav.sidebar)
    await page.waitForSelector('nav.sidebar', { state: 'visible' });

    // Use a selector based on the cog icon and text "Settings" (li.nav-item with .fa-cog and text)
    // Try to locate the settings button by icon and text, not just ID
    const settingsBtn = await page.waitForSelector(
      '//li[contains(@class, "nav-item")]//span[normalize-space(text())="Settings"]/preceding-sibling::i[contains(@class, "fa-cog")]/parent::li',
      { timeout: 3000 }
    ).catch(async () => {
      // Fallback: try by text only
      return await page.waitForSelector('li.nav-item:has-text("Settings")', { timeout: 2000 });
    });

    // Take a screenshot before clicking settings
    const beforeClickShot = path.join(screenshotsDir, 'step_2_before_settings_click.png');
    await page.screenshot({ path: beforeClickShot });
    screenshots.push(beforeClickShot);

    // Click the settings button
    await settingsBtn.click();

    // Wait for the settings modal to appear (div#settings-modal.modal.active)
    await page.waitForSelector('#settings-modal.modal.active', { timeout: 4000 });

    // Wait for the modal header with text "Settings" (robust check)
    await page.waitForSelector('#settings-modal .modal-header h3', { state: 'visible', timeout: 2000 });

    // Take a screenshot of the open settings modal
    const afterOpenShot = path.join(screenshotsDir, 'step_3_settings_modal_open.png');
    await page.screenshot({ path: afterOpenShot });
    screenshots.push(afterOpenShot);

    // Extract some data from the modal to verify it's open (e.g., tab names)
    const tabTexts = await page.$$eval(
      '#settings-modal .settings-tabs .tab-btn',
      tabs => tabs.map(tab => tab.textContent.trim())
    );

    // Also extract the modal title
    const modalTitle = await page.$eval(
      '#settings-modal .modal-header h3',
      el => el.textContent.trim()
    );

    return {
      success: true,
      action: "Opened the Settings modal by clicking the sidebar Settings button. Verified modal is open and extracted tab names.",
      extractedData: {
        modalTitle,
        settingsTabs: tabTexts
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
