import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // 1. Open the Settings modal
    // Wait for the settings button in the sidebar and click it
    await page.waitForSelector('#settings-btn', { state: 'visible' });
    await page.click('#settings-btn');

    // Wait for the settings modal to appear
    await page.waitForSelector('#settings-modal.modal.active', { timeout: 3000 });

    // Take screenshot after opening settings
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_settings_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_settings_open.png'));

    // 2. Switch to the "Appearance" tab if not already active
    // Find the "Appearance" tab button by text
    const appearanceTabBtn = await page.$('//button[contains(@class,"tab-btn") and normalize-space(text())="Appearance"]');
    if (appearanceTabBtn) {
      // Only click if not already active
      const className = await appearanceTabBtn.getAttribute('class');
      if (!className.includes('active')) {
        await appearanceTabBtn.click();
        // Wait for the appearance settings section to be visible
        await page.waitForSelector('#appearance-settings', { state: 'visible' });
      }
    }

    // Take screenshot after switching to Appearance tab
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_appearance_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_appearance_tab.png'));

    // 3. Change the Theme setting
    // Wait for the theme select dropdown
    await page.waitForSelector('#theme-select');
    // Get the current value
    const currentTheme = await page.$eval('#theme-select', el => el.value);

    // Decide the new theme (toggle: if 'dark', set to 'light', else set to 'dark')
    const newTheme = currentTheme === 'dark' ? 'light' : 'dark';

    // Select the new theme
    await page.selectOption('#theme-select', newTheme);

    // Take screenshot after changing the theme selection
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_theme_changed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_theme_changed.png'));

    // 4. Save the settings
    await page.waitForSelector('#save-settings', { state: 'visible' });
    await page.click('#save-settings');

    // Wait for the modal to close (modal should not have 'active' class anymore)
    await page.waitForSelector('#settings-modal', { state: 'visible' });
    await page.waitForSelector('#settings-modal.active', { state: 'detached', timeout: 3000 });

    // Wait a moment for the theme to apply
    await page.waitForTimeout(500);

    // Take screenshot after saving and applying theme
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_theme_applied.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_theme_applied.png'));

    // 5. Extract data to verify theme change
    // Check if body has the light-theme class for 'light', or not for 'dark'
    const bodyClass = await page.$eval('body', el => el.className);

    let themeApplied = null;
    if (newTheme === 'light') {
      themeApplied = bodyClass.includes('light-theme') ? 'light' : 'not-applied';
    } else {
      themeApplied = !bodyClass.includes('light-theme') ? 'dark' : 'not-applied';
    }

    return {
      success: themeApplied === newTheme,
      action: `Changed theme setting to '${newTheme}' and verified application`,
      extractedData: {
        previousTheme: currentTheme,
        newTheme,
        bodyClass,
        themeApplied
      },
      screenshots,
      error: themeApplied !== newTheme ? `Theme '${newTheme}' was not applied correctly.` : null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // 1. Open the Settings modal via the sidebar cog icon (use #settings-btn)
    await page.waitForSelector('#settings-btn', { state: 'visible' });
    await page.click('#settings-btn');

    // Wait for the modal to appear using its heading text
    await page.waitForSelector('//div[contains(@class,"modal-header")]//h3[contains(text(),"Settings")]', { timeout: 5000 });

    // Take screenshot after opening settings
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_settings_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_settings_open.png'));

    // 2. Switch to the "Appearance" tab using text selector (tab button with text "Appearance")
    const appearanceTab = await page.waitForSelector('//div[contains(@class,"settings-tabs")]//button[contains(., "Appearance")]', { timeout: 3000 });
    await appearanceTab.click();

    // Wait for the appearance section to be visible
    await page.waitForSelector('#appearance-settings', { state: 'visible' });

    // Take screenshot after switching to Appearance tab
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_appearance_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_appearance_tab.png'));

    // 3. Change the Theme dropdown to "Light"
    await page.waitForSelector('#theme-select', { state: 'visible' });
    await page.selectOption('#theme-select', 'light');

    // Take screenshot after changing theme selection
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_theme_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_theme_selected.png'));

    // 4. Save the settings (button with id #save-settings)
    await page.waitForSelector('#save-settings', { state: 'visible' });
    await page.click('#save-settings');

    // Wait for the modal to close (modal should not be visible)
    await page.waitForSelector('#settings-modal', { state: 'hidden', timeout: 5000 });

    // Wait for the body to have the light-theme class (verifies theme change)
    await page.waitForFunction(() => document.body.classList.contains('light-theme'));

    // Take screenshot after theme applied
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_theme_applied.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_theme_applied.png'));

    // Extract data: check if body has light-theme class and theme-select value is "light"
    const themeClassApplied = await page.evaluate(() => document.body.classList.contains('light-theme'));
    const themeSelectValue = await page.evaluate(() => {
      const sel = document.querySelector('#theme-select');
      return sel ? sel.value : null;
    });

    return {
      success: themeClassApplied && themeSelectValue === 'light',
      action: "Changed theme setting to Light via settings modal and verified light theme is applied",
      extractedData: {
        themeClassApplied,
        themeSelectValue
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const step1 = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: step1 });
    screenshots.push(step1);

    // 1. Open the Settings Modal using a unique selector (ID and icon)
    // Wait for the sidebar to be visible
    await page.waitForSelector('nav.sidebar');
    // Use the settings nav-item by its icon and text, as an alternative to just ID
    const settingsBtn = await page.waitForSelector('//nav[contains(@class,"sidebar")]//li[contains(@class,"nav-item")]//span[text()="Settings"]/preceding-sibling::i[contains(@class,"fa-cog")]/parent::li', { timeout: 5000 });
    await settingsBtn.click();

    // Wait for the modal to appear (by modal header text)
    await page.waitForSelector('//div[contains(@class,"modal-header")]/h3[text()="Settings"]', { timeout: 5000 });

    // Screenshot after opening settings
    const step2 = path.join(screenshotsDir, 'step_2_settings_open.png');
    await page.screenshot({ path: step2 });
    screenshots.push(step2);

    // 2. Switch to the "Appearance" tab using a different selector (tab button by text)
    const appearanceTab = await page.waitForSelector('//div[contains(@class,"settings-tabs")]//button[contains(@class,"tab-btn") and text()="Appearance"]', { timeout: 5000 });
    await appearanceTab.click();

    // Wait for the appearance section to be visible (by h4 text)
    await page.waitForSelector('//div[contains(@class,"settings-section") and .//h4[text()="Appearance"]]', { timeout: 5000 });

    // Screenshot after switching to Appearance tab
    const step3 = path.join(screenshotsDir, 'step_3_appearance_tab.png');
    await page.screenshot({ path: step3 });
    screenshots.push(step3);

    // 3. Change the Theme setting
    // Use the label text to find the theme select
    const themeLabel = await page.waitForSelector('//div[contains(@class,"settings-section") and .//h4[text()="Appearance"]]//label[text()="Theme"]', { timeout: 5000 });
    // Get the select element following the label
    const themeSelect = await themeLabel.evaluateHandle(label => label.parentElement.querySelector('select'));
    // Change theme to "Light"
    await themeSelect.selectOption('light');

    // Wait a moment for theme to apply
    await page.waitForTimeout(500);

    // Screenshot after selecting Light theme
    const step4 = path.join(screenshotsDir, 'step_4_theme_selected.png');
    await page.screenshot({ path: step4 });
    screenshots.push(step4);

    // 4. Save the settings (use button by text)
    const saveBtn = await page.waitForSelector('//div[contains(@class,"settings-actions")]//button[contains(@class,"save-btn") and text()="Save Changes"]', { timeout: 5000 });
    await saveBtn.click();

    // Wait for modal to close (modal not visible)
    await page.waitForSelector('#settings-modal', { state: 'hidden', timeout: 5000 });

    // Wait for body to have light-theme class (verifying theme applied)
    await page.waitForFunction(() => document.body.classList.contains('light-theme'), {}, { timeout: 3000 });

    // Screenshot after theme applied and modal closed
    const step5 = path.join(screenshotsDir, 'step_5_theme_applied.png');
    await page.screenshot({ path: step5 });
    screenshots.push(step5);

    // Extract data: check body class and current theme select value
    const bodyClass = await page.evaluate(() => document.body.className);
    // Re-open settings to confirm select value
    const settingsBtn2 = await page.waitForSelector('//nav[contains(@class,"sidebar")]//li[contains(@class,"nav-item")]//span[text()="Settings"]/preceding-sibling::i[contains(@class,"fa-cog")]/parent::li', { timeout: 5000 });
    await settingsBtn2.click();
    await page.waitForSelector('//div[contains(@class,"modal-header")]/h3[text()="Settings"]', { timeout: 5000 });
    const appearanceTab2 = await page.waitForSelector('//div[contains(@class,"settings-tabs")]//button[contains(@class,"tab-btn") and text()="Appearance"]', { timeout: 5000 });
    await appearanceTab2.click();
    const themeValue = await page.$eval('#theme-select', el => el.value);

    // Screenshot of settings reopened for verification
    const step6 = path.join(screenshotsDir, 'step_6_verify_theme.png');
    await page.screenshot({ path: step6 });
    screenshots.push(step6);

    return {
      success: true,
      action: "Changed theme setting to Light via Settings > Appearance and verified the theme was applied.",
      extractedData: {
        bodyClass,
        themeSelectValue: themeValue
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
