import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open settings modal
    await page.click('#settings-btn');
    await page.waitForSelector('#settings-modal', { state: 'visible' });

    // Take screenshot of settings modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_settings_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_settings_modal.png'));

    // Change theme setting
    await page.selectOption('#theme-select', 'light');
    await page.click('#save-settings');

    // Wait for the settings to be saved and modal to close
    await page.waitForSelector('#settings-modal', { state: 'hidden' });

    // Take screenshot after saving settings
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_saving.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_saving.png'));

    // Verify the theme change
    const bodyClass = await page.evaluate(() => document.body.className);
    const themeChanged = bodyClass.includes('light-theme');

    return {
      success: true,
      action: "Changed theme setting to light mode",
      extractedData: { themeChanged },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

