import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open settings modal by clicking the settings button
    await page.waitForSelector('#settings-btn');
    await page.click('#settings-btn');

    // Wait for the settings modal to be visible
    await page.waitForSelector('#settings-modal', { state: 'visible' });

    // Take a screenshot of the settings modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_settings_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_settings_modal_opened.png'));

    // Change the theme setting to 'light'
    await page.selectOption('#theme-select', 'light');

    // Take a screenshot after changing the theme
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_theme_changed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_theme_changed.png'));

    // Save changes
    await page.click('#save-settings');

    // Wait for a confirmation message or some indication that settings have been saved
    await page.waitForSelector('.toast-success', { state: 'visible' });

    // Take a screenshot of the success message
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_settings_saved.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_settings_saved.png'));

    return {
      success: true,
      action: "Changed theme setting to light and saved changes",
      extractedData: {
        theme: await page.$eval('#theme-select', el => el.value)
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
