import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the emoji button in the compose area
    await page.waitForSelector('#add-emoji');
    await page.click('#add-emoji');

    // Wait for emoji picker modal to appear
    await page.waitForSelector('#emoji-picker-modal.active');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_picker_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_picker_open.png'));

    // Wait for emojis to load and select the first emoji
    await page.waitForSelector('#emoji-grid .emoji-item');
    await page.click('#emoji-grid .emoji-item:first-child');

    // Wait for emoji picker to close and verify emoji was added
    await page.waitForSelector('#emoji-picker-modal:not(.active)', { timeout: 5000 });

    // Get the text from the thread text area to verify emoji was added
    const threadText = await page.$eval('#thread-text', el => el.value);

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_added.png'));

    return {
      success: true,
      action: "Successfully added emoji to thread composition",
      extractedData: {
        threadText: threadText,
        emojiAdded: threadText.length > 0
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to add emoji to thread",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the compose thread textarea to be visible
    await page.waitForSelector('#thread-text', { timeout: 10000 });

    // Click the emoji button to open the emoji picker
    await page.click('#add-emoji');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_button_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_button_clicked.png'));

    // Wait for emoji picker modal to appear
    await page.waitForSelector('#emoji-picker-modal', { timeout: 10000 });
    await page.waitForSelector('.emoji-item', { timeout: 10000 });

    // Take screenshot of emoji picker
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_picker_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_picker_open.png'));

    // Select the first emoji in the grid
    const firstEmoji = await page.$('.emoji-item');
    const emojiText = await firstEmoji.textContent();
    await firstEmoji.click();

    // Wait for emoji picker to close and verify emoji was added
    await page.waitForSelector('#emoji-picker-modal', { state: 'hidden', timeout: 5000 });

    // Get the textarea content to verify emoji was added
    const textareaContent = await page.$eval('#thread-text', el => el.value);

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_emoji_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_emoji_added.png'));

    return {
      success: true,
      action: "Successfully added emoji to thread composer",
      extractedData: {
        emojiUsed: emojiText.trim(),
        finalTextContent: textareaContent
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot if possible
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error_state.png') });
      screenshots.push(path.join(screenshotsDir, 'error_state.png'));
    } catch (e) {
      console.log('Could not take error screenshot:', e.message);
    }

    return {
      success: false,
      action: "Failed to add emoji to thread",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load
    await page.waitForSelector('.thread-item', { timeout: 10000 });

    // Find first thread and click reply button
    const firstThread = await page.$('.thread-item');
    const replyButton = await firstThread.$('[data-action="reply"]');
    await replyButton.click();

    // Wait for reply modal to appear
    await page.waitForSelector('#reply-modal.active', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_reply_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_reply_modal.png'));

    // Click emoji button in reply modal
    const emojiButton = await page.$('#add-reply-emoji');
    await emojiButton.click();

    // Wait for emoji picker to appear
    await page.waitForSelector('#emoji-picker-modal.active', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_picker.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_picker.png'));

    // Select first emoji
    const firstEmoji = await page.$('.emoji-item');
    const emojiText = await firstEmoji.textContent();
    await firstEmoji.click();

    // Verify emoji was added to reply text
    const replyTextarea = await page.$('#reply-text');
    const replyContent = await replyTextarea.inputValue();

    // Post the reply
    const postButton = await page.$('#post-reply');
    await postButton.click();

    // Wait for success toast
    await page.waitForSelector('.toast-success', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_reply_posted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_reply_posted.png'));

    return {
      success: true,
      action: "Successfully added emoji to thread reply and posted",
      extractedData: {
        emojiUsed: emojiText.trim(),
        replyContent: replyContent
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to add emoji to thread",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
