import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the thread compose textarea to be visible
    await page.waitForSelector('#thread-text', { state: 'visible' });

    // Focus the thread textarea and type some text
    await page.click('#thread-text');
    await page.type('#thread-text', 'Testing emoji: ');

    // Take screenshot after typing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_typed_text.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_typed_text.png'));

    // Click the "Add emoji" button (by id)
    await page.waitForSelector('#add-emoji', { state: 'visible' });
    await page.click('#add-emoji');

    // Wait for the emoji picker modal to appear
    await page.waitForSelector('#emoji-picker-modal.modal.active', { timeout: 3000 });

    // Take screenshot of emoji picker open
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_picker_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_picker_open.png'));

    // Wait for emoji grid to load and pick a specific emoji (e.g., ðŸ˜€)
    await page.waitForSelector('#emoji-grid .emoji-item', { timeout: 2000 });

    // Find the first emoji (ðŸ˜€) and click it
    const emojiHandle = await page.$('#emoji-grid .emoji-item');
    const emojiValue = await emojiHandle.evaluate(el => el.textContent);

    await emojiHandle.click();

    // Wait for emoji picker modal to close
    await page.waitForSelector('#emoji-picker-modal', { state: 'hidden', timeout: 2000 });

    // Take screenshot after emoji inserted
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_emoji_inserted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_emoji_inserted.png'));

    // Extract the value of the textarea to verify emoji was inserted
    const threadTextValue = await page.$eval('#thread-text', el => el.value);

    // Post the thread
    await page.waitForSelector('#post-thread:not([disabled])');
    await page.click('#post-thread');

    // Wait for the thread to appear in the feed (top thread should contain our text)
    await page.waitForTimeout(500); // allow UI to update

    // Find the first thread text in the feed
    const firstThreadSelector = '#threads-feed .thread-item .thread-text';
    await page.waitForSelector(firstThreadSelector, { timeout: 2000 });
    const firstThreadHtml = await page.$eval(firstThreadSelector, el => el.innerText);

    // Take screenshot of the feed after posting
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_thread_posted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_thread_posted.png'));

    // Verify the emoji is present in the posted thread
    const emojiInTextarea = threadTextValue.includes(emojiValue);
    const emojiInFeed = firstThreadHtml.includes(emojiValue);

    return {
      success: emojiInTextarea && emojiInFeed,
      action: "Added emoji to thread using emoji picker and posted the thread. Verified emoji in textarea and feed.",
      extractedData: {
        emojiUsed: emojiValue,
        textareaValue: threadTextValue,
        feedFirstThread: firstThreadHtml,
        emojiInTextarea,
        emojiInFeed
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Step 1: Initial screenshot of the home page
    const initialShot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialShot });
    screenshots.push(initialShot);

    // Step 2: Wait for the "Start a thread..." textarea using placeholder attribute
    const textareaSelector = 'textarea[placeholder="Start a thread..."]';
    await page.waitForSelector(textareaSelector, { state: 'visible' });

    // Step 3: Click the emoji button using aria-label and icon class as fallback
    // Try to use the button with title "Add emoji"
    let emojiBtn = await page.$('button[title="Add emoji"]');
    if (!emojiBtn) {
      // Fallback: select by icon class and tool-btn
      emojiBtn = await page.$('button.tool-btn:has(i.fas.fa-smile)');
    }
    if (!emojiBtn) throw new Error('Emoji button not found');
    await emojiBtn.click();

    // Step 4: Wait for the emoji picker modal to appear by its id and header text
    await page.waitForSelector('#emoji-picker-modal.modal.active', { timeout: 3000 });
    // Wait for emoji grid to be visible
    await page.waitForSelector('#emoji-grid .emoji-item', { timeout: 3000 });

    // Step 5: Click on the first emoji in the grid (for determinism)
    const firstEmoji = await page.$('#emoji-grid .emoji-item');
    if (!firstEmoji) throw new Error('No emoji found in picker');
    // Get the emoji character for later verification
    const emojiChar = await firstEmoji.evaluate(el => el.textContent);
    await firstEmoji.click();

    // Step 6: Wait for emoji picker modal to close
    await page.waitForSelector('#emoji-picker-modal', { state: 'hidden', timeout: 2000 });

    // Step 7: Verify emoji is inserted into the textarea
    // Wait for textarea value to update
    await page.waitForFunction(
      (selector, emoji) => {
        const el = document.querySelector(selector);
        return el && el.value.includes(emoji);
      },
      textareaSelector,
      emojiChar
    );

    // Screenshot after emoji insertion
    const emojiInsertedShot = path.join(screenshotsDir, 'step_2_emoji_inserted.png');
    await page.screenshot({ path: emojiInsertedShot });
    screenshots.push(emojiInsertedShot);

    // Step 8: Extract the textarea value for verification
    const textareaValue = await page.$eval(textareaSelector, el => el.value);

    return {
      success: true,
      action: "Added an emoji to the thread compose textarea using the emoji picker.",
      extractedData: {
        emojiInserted: emojiChar,
        textareaValue
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Wait for the compose thread textarea using placeholder attribute
    await page.waitForSelector('textarea[placeholder="Start a thread..."]', { timeout: 5000 });

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Focus the textarea and type some text
    const textarea = await page.$('textarea[placeholder="Start a thread..."]');
    await textarea.click();
    await textarea.type('Testing emoji automation ');

    // Wait for the emoji button using aria-label or title attribute (different selector)
    const emojiBtn = await page.waitForSelector('button[title="Add emoji"]', { timeout: 5000 });
    // Scroll into view in case it's hidden
    await emojiBtn.evaluate(node => node.scrollIntoView());
    await emojiBtn.click();

    // Wait for the emoji picker modal to appear (using its heading text)
    await page.waitForSelector('div.modal.active .modal-header h3', { timeout: 5000 });
    // Take screenshot of emoji picker open
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_picker_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_picker_open.png'));

    // Wait for emoji grid and pick the first emoji (using .emoji-item class)
    await page.waitForSelector('.emoji-grid .emoji-item', { timeout: 5000 });
    // Select the first emoji (for robustness, get the text content)
    const firstEmoji = await page.$('.emoji-grid .emoji-item');
    const emojiChar = await firstEmoji.textContent();
    await firstEmoji.click();

    // Wait for emoji picker to close (modal not active)
    await page.waitForSelector('#emoji-picker-modal', { state: 'hidden', timeout: 3000 });

    // Take screenshot after emoji inserted
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_inserted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_inserted.png'));

    // Extract the textarea value to verify emoji was inserted
    const threadTextValue = await page.$eval('textarea[placeholder="Start a thread..."]', el => el.value);

    // Post the thread
    const postBtn = await page.waitForSelector('#post-thread', { timeout: 5000 });
    // Wait for button to be enabled
    await postBtn.waitForElementState('enabled', { timeout: 3000 });
    await postBtn.click();

    // Wait for the thread to appear in the feed (look for the text we just posted)
    // Use XPath to find a thread-text div containing our text (with emoji)
    const expectedText = 'Testing emoji automation ' + emojiChar;
    // Wait for a thread containing this text (may need to allow for whitespace/emoji issues)
    await page.waitForSelector(`.thread-text:has-text("${expectedText}")`, { timeout: 5000 });

    // Take screenshot of the new thread in the feed
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_thread_with_emoji.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_thread_with_emoji.png'));

    // Extract the content of the first thread in the feed for verification
    const firstThreadContent = await page.$eval('.threads-feed .thread-item .thread-text', el => el.textContent);

    return {
      success: true,
      action: "Added an emoji to a new thread using the emoji picker and posted it. Verified emoji presence in thread feed.",
      extractedData: {
        emojiInserted: emojiChar,
        threadTextValue,
        firstThreadContent
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
