import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for suggested accounts section to load
    await page.waitForSelector('.suggested-accounts', { timeout: 10000 });

    // Take screenshot of suggested accounts section
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_suggested_accounts.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_suggested_accounts.png'));

    // Find the first suggested account that isn't already being followed
    const accountElement = await page.$('.account-item:not(:has(.follow-btn.following))');

    if (!accountElement) {
      throw new Error('No suggested accounts available to follow');
    }

    // Extract account information before following
    const accountInfo = await accountElement.evaluate(el => {
      const name = el.querySelector('.account-name').textContent;
      const handle = el.querySelector('.account-handle').textContent;
      return { name, handle };
    });

    // Click the follow button
    const followButton = await accountElement.$('.follow-btn');
    await followButton.click();

    // Wait for the button to update to "Following" state
    await page.waitForFunction(
      (button) => button.textContent === 'Following',
      {},
      followButton
    );

    // Take screenshot after following
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_followed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_followed.png'));

    return {
      success: true,
      action: "Successfully followed suggested user",
      extractedData: {
        followedUser: accountInfo.name,
        userHandle: accountInfo.handle,
        timestamp: new Date().toISOString()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to follow suggested user",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

