import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the suggested accounts section in the right sidebar
    await page.waitForSelector('#suggested-accounts', { timeout: 5000 });

    // Wait for at least one suggested account to be rendered (dynamic content)
    await page.waitForSelector('#suggested-accounts .account-item', { timeout: 5000 });

    // Take screenshot before follow
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_suggested_accounts.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_suggested_accounts.png'));

    // Find the first suggested account's follow button
    const followBtn = await page.$('#suggested-accounts .account-item .follow-btn:not(.following)');
    if (!followBtn) {
      throw new Error('No unfollowed suggested account found to follow');
    }

    // Extract username before following
    const accountItem = await followBtn.evaluateHandle(node => node.closest('.account-item'));
    const username = await accountItem.evaluate(node => node.querySelector('.account-name')?.textContent?.trim());

    // Click the follow button
    await followBtn.click();

    // Wait for the button to change to "Following"
    await page.waitForFunction(
      btn => btn.classList.contains('following') && btn.textContent.trim().toLowerCase() === 'following',
      followBtn,
      { timeout: 3000 }
    );

    // Take screenshot after follow
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_follow.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_follow.png'));

    // Extract the button text after following
    const btnText = await followBtn.evaluate(btn => btn.textContent.trim());

    // Extract all suggested accounts and their follow status for verification
    const suggestedAccounts = await page.$$eval('#suggested-accounts .account-item', items =>
      items.map(item => ({
        username: item.querySelector('.account-name')?.textContent?.trim(),
        handle: item.querySelector('.account-handle')?.textContent?.trim(),
        isFollowing: item.querySelector('.follow-btn')?.classList.contains('following'),
        btnText: item.querySelector('.follow-btn')?.textContent?.trim()
      }))
    );

    return {
      success: true,
      action: `Followed suggested user "${username}" via the 'Follow' button in the right sidebar.`,
      extractedData: {
        followedUsername: username,
        followButtonTextAfter: btnText,
        suggestedAccounts
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

