    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the right sidebar and "Suggested for you" section to be visible
    await page.waitForSelector('.right-sidebar', { timeout: 5000 });
    await page.waitForSelector('h3:text("Suggested for you")', { timeout: 5000 });

    // Wait for at least one suggested account to appear
    await page.waitForSelector('#suggested-accounts .account-item', { timeout: 5000 });

    // Take screenshot of suggested accounts list
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_suggested_accounts.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_suggested_accounts.png'));

    // Find the first "Follow" button in the suggested accounts list using button text and parent structure
    const followButton = await page.locator('#suggested-accounts .account-item button.follow-btn:not(.following)').first();
    await followButton.waitFor({ state: 'visible', timeout: 3000 });

    // Extract the username before following for verification
    const accountItem = await followButton.locator('..').locator('..'); // Go up to .account-item
    const username = await accountItem.locator('.account-name').innerText();
    const handle = await accountItem.locator('.account-handle').innerText();

    // Click the Follow button
    await followButton.click();

    // Wait for the button text to change to "Following"
    await followButton.waitFor({ state: 'attached', timeout: 3000 });
    await page.waitForFunction(
      (el) => el.textContent.trim().toLowerCase() === 'following',
      followButton,
      { timeout: 3000 }
    );

    // Take screenshot after following
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_followed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_followed.png'));

    // Extract the button text after following
    const buttonText = await followButton.textContent();

    // Prepare extracted data for verification
    const extractedData = {
      suggestedUser: {
        username: username.trim(),
        handle: handle.trim(),
        followButtonText: buttonText.trim()
      }
    };

    return {
      success: true,
      action: `Followed the first suggested user (${username.trim()} ${handle.trim()}) in the right sidebar`,
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const step1 = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: step1 });
    screenshots.push(step1);

    // Wait for the right sidebar to be present (by h3 text "Suggested for you")
    await page.waitForSelector('aside.right-sidebar');
    await page.waitForSelector('//h3[contains(text(), "Suggested for you")]', { timeout: 5000 });

    // Wait for suggested accounts to load (wait for at least one .account-item)
    await page.waitForSelector('.suggested-accounts .account-list .account-item', { timeout: 5000 });

    // Take screenshot of suggested accounts list
    const step2 = path.join(screenshotsDir, 'step_2_suggested_accounts.png');
    await page.screenshot({ path: step2 });
    screenshots.push(step2);

    // Find the first "Follow" button in the suggested accounts list
    // Use a selector that matches the button text and avoids relying on class only
    const followButton = await page.locator(
      '//div[contains(@class,"suggested-accounts")]//button[contains(@class,"follow-btn") and normalize-space(text())="Follow"]'
    ).first();

    // Ensure the button is visible
    await followButton.waitFor({ state: 'visible', timeout: 5000 });

    // Extract the username of the suggested user to follow (for verification)
    const accountItem = await followButton.locator('..').locator('..');
    const username = await accountItem.locator('.account-name').textContent();
    const userHandle = await accountItem.locator('.account-handle').textContent();

    // Click the "Follow" button
    await followButton.click();

    // Wait for the button text to change to "Following"
    await followButton.waitFor({ state: 'attached', timeout: 3000 });
    await page.waitForFunction(
      btn => btn.textContent.trim().toLowerCase() === 'following',
      followButton
    );

    // Take screenshot after following
    const step3 = path.join(screenshotsDir, 'step_3_followed.png');
    await page.screenshot({ path: step3 });
    screenshots.push(step3);

    // Extract the button text to verify
    const buttonText = await followButton.textContent();

    // Return result
    return {
      success: true,
      action: `Followed suggested user "${username}" (${userHandle}) by clicking the Follow button. Button text after click: "${buttonText.trim()}"`,
      extractedData: {
        followedUsername: username ? username.trim() : null,
        followedUserHandle: userHandle ? userHandle.trim() : null,
        followButtonText: buttonText ? buttonText.trim() : null
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
