import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the sidebar nav menu to be visible
    await page.waitForSelector('.nav-menu');

    // Find the "Search" nav tab in the sidebar and click it
    // Try by text content first, fallback to data-view attribute
    const searchNavSelector = '.nav-item[data-view="search"]';
    await page.waitForSelector(searchNavSelector);
    const searchNav = await page.$(searchNavSelector);
    if (!searchNav) throw new Error('Search tab not found in sidebar');
    await searchNav.click();

    // Wait for the main content area to switch to the search view
    await page.waitForSelector('#search-view.view.active', { timeout: 3000 });

    // Take screenshot after switching to Search tab
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_tab_active.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_tab_active.png'));

    // Wait for the search tabs to be visible
    await page.waitForSelector('.search-tabs .tab-btn');

    // Extract the active tab's label
    const activeTabText = await page.$eval('.search-tabs .tab-btn.active', el => el.textContent.trim());

    // Now switch to the "People" tab
    const peopleTabBtn = await page.$('.search-tabs .tab-btn[data-tab="people"]');
    if (!peopleTabBtn) throw new Error('People tab not found in search tabs');
    await peopleTabBtn.click();

    // Wait for the "People" tab to become active
    await page.waitForSelector('.search-tabs .tab-btn[data-tab="people"].active', { timeout: 2000 });

    // Take screenshot after switching to People tab
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_people_tab_active.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_people_tab_active.png'));

    // Extract the new active tab's label
    const newActiveTabText = await page.$eval('.search-tabs .tab-btn.active', el => el.textContent.trim());

    // Prepare extracted data for verification
    const extractedData = {
      initialActiveTab: activeTabText,
      newActiveTab: newActiveTabText,
      tabs: await page.$$eval('.search-tabs .tab-btn', els => els.map(e => ({
        text: e.textContent.trim(),
        active: e.classList.contains('active')
      })))
    };

    return {
      success: true,
      action: "Switched to the Search tab, then switched the search sub-tab to 'People'. Verified tab switch.",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

