    const searchTab = await page.locator('//li[contains(@class,"nav-item") and @data-view="search" and .//span[text()="Search"]]');
    await searchTab.waitFor({ state: 'visible' });

    // Click the "Search" tab
    await searchTab.click();

    // Wait for the main-content area to switch to the search view
    // The #search-view should have class "active"
    await page.waitForSelector('#search-view.view.active', { timeout: 3000 });

    // Take screenshot after switching to Search tab
    const step2 = path.join(screenshotsDir, 'step_2_search_tab_active.png');
    await page.screenshot({ path: step2 });
    screenshots.push(step2);

    // Wait for the search tabs to be visible
    await page.waitForSelector('.search-tabs .tab-btn', { timeout: 2000 });

    // Switch to the "People" tab using its data-tab attribute and text
    const peopleTab = await page.locator('//div[contains(@class,"search-tabs")]//button[@data-tab="people" and contains(text(),"People")]');
    await peopleTab.waitFor({ state: 'visible' });
    await peopleTab.click();

    // Wait for the "People" tab to have the "active" class
    await page.waitForSelector('.search-tabs .tab-btn[data-tab="people"].active', { timeout: 2000 });

    // Take screenshot after switching to People tab
    const step3 = path.join(screenshotsDir, 'step_3_people_tab_active.png');
    await page.screenshot({ path: step3 });
    screenshots.push(step3);

    // Extract which tab is active and the tab labels for verification
    const activeTabLabel = await page.$eval('.search-tabs .tab-btn.active', el => el.textContent.trim());
    const allTabLabels = await page.$$eval('.search-tabs .tab-btn', els => els.map(e => ({
      label: e.textContent.trim(),
      active: e.classList.contains('active'),
      dataTab: e.getAttribute('data-tab')
    })));

    return {
      success: true,
      action: "Switched to the Search view and activated the 'People' search tab",
      extractedData: {
        activeTabLabel,
        allTabLabels
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    const screenshots = [];

    // Take initial screenshot
    const initialShot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialShot });
    screenshots.push(initialShot);

    // Wait for the sidebar nav-menu to ensure DOM is ready
    await page.waitForSelector('nav.sidebar ul.nav-menu');

    // Find the "Search" tab in the sidebar using text and class
    // Use a different selector: li[data-view="search"] span:text("Search")
    // But Playwright doesn't support :text(), so use XPath for robustness
    const searchTab = await page.waitForSelector('//nav[contains(@class,"sidebar")]//li[@data-view="search"]//span[contains(text(),"Search")]', { timeout: 5000 });

    // Scroll into view if needed
    await searchTab.scrollIntoViewIfNeeded();

    // Click the "Search" tab
    await searchTab.click();

    // Wait for the search view to become active (div#search-view.view.active)
    await page.waitForSelector('div#search-view.view.active', { timeout: 3000 });

    // Wait for the search tabs to appear
    await page.waitForSelector('div.search-tabs button.tab-btn', { timeout: 3000 });

    // Take screenshot after switching to Search tab
    const afterSwitchShot = path.join(screenshotsDir, 'step_2_search_tab_active.png');
    await page.screenshot({ path: afterSwitchShot });
    screenshots.push(afterSwitchShot);

    // Extract the active tab text in search (should be "All" by default)
    const activeTabText = await page.$eval(
      'div#search-view .search-tabs button.tab-btn.active',
      el => el.textContent.trim()
    );

    // Extract the search input placeholder to verify search UI is visible
    const searchInputPlaceholder = await page.$eval(
      'div#search-view input#search-input',
      el => el.getAttribute('placeholder')
    );

    // Extract all tab names in the search tabs
    const tabNames = await page.$$eval(
      'div#search-view .search-tabs button.tab-btn',
      nodes => nodes.map(n => n.textContent.trim())
    );

    return {
      success: true,
      action: "Switched to the Search tab in the sidebar and verified the Search view is active.",
      extractedData: {
        activeTabText,
        searchInputPlaceholder,
        tabNames
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
