import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Switch to the Search tab
    await page.waitForSelector('.nav-item[data-view="search"]');
    await page.click('.nav-item[data-view="search"]');
    await page.waitForLoadState('networkidle');

    // Take screenshot after switching to the Search tab
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_tab.png'));

    // Verify that the Search view is active
    const isSearchViewActive = await page.isVisible('#search-view.active');
    if (!isSearchViewActive) {
      throw new Error("Search view is not active after switching tabs.");
    }

    // Extract data to verify task completion
    const searchTabTitle = await page.textContent('.content-header h2');
    const extractedData = {
      title: searchTabTitle,
      isSearchViewActive
    };

    return {
      success: true,
      action: "Switched to the Search tab successfully",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the "Search" tab
    await page.waitForSelector('li[data-view="search"]');
    await page.click('li[data-view="search"]');

    // Wait for the search view to be visible
    await page.waitForSelector('#search-view');

    // Take a screenshot after switching to the search tab
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_tab.png'));

    // Enter a search query
    await page.waitForSelector('#search-input');
    await page.fill('#search-input', 'example query');

    // Wait for search results to load
    await page.waitForSelector('.search-result-item');

    // Take a screenshot of the search results
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_results.png'));

    // Extract data from search results
    const results = await page.evaluate(() => {
      const items = Array.from(document.querySelectorAll('.search-result-item'));
      return items.map(item => ({
        username: item.querySelector('h4').innerText,
        handle: item.querySelector('p').innerText,
      }));
    });

    return {
      success: true,
      action: "Switched to search tab and performed a search",
      extractedData: { results },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the sidebar to be visible and click on the Search tab
    await page.waitForSelector('.nav-item[data-view="search"]', { timeout: 5000 });
    await page.click('.nav-item[data-view="search"]');

    // Take a screenshot after switching to the Search tab
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_tab.png'));

    // Wait for the search view to be visible
    await page.waitForSelector('#search-view', { timeout: 5000 });

    // Verify that the search input is present
    const searchInputVisible = await page.isVisible('#search-input');
    const searchTabsVisible = await page.isVisible('.search-tabs');

    // Extract meaningful data to verify task completion
    const extractedData = {
      searchInputVisible,
      searchTabsVisible,
    };

    // Take a final screenshot of the search view
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_final_search_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_final_search_view.png'));

    return {
      success: true,
      action: "Switched to the Search tab successfully",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
