import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the thread textarea to be visible
    await page.waitForSelector('#thread-text');

    // Get initial character count
    const initialCount = await page.$eval('#char-count', el => el.textContent);

    // Type some text to test character count
    await page.fill('#thread-text', 'Testing character count functionality');

    // Wait for the character count to update
    await page.waitForTimeout(500);

    // Take screenshot after typing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_typing.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_typing.png'));

    // Get updated character count
    const updatedCount = await page.$eval('#char-count', el => el.textContent);

    // Test near-limit character count
    const longText = 'A'.repeat(450); // Close to 500 limit
    await page.fill('#thread-text', longText);
    await page.waitForTimeout(500);

    // Take screenshot with near-limit text
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_near_limit.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_near_limit.png'));

    // Check if warning class is applied
    const hasWarningClass = await page.$eval('#char-count', el => el.classList.contains('warning'));

    // Test exceeding character limit
    const exceededText = 'B'.repeat(510); // Exceed 500 limit
    await page.fill('#thread-text', exceededText);
    await page.waitForTimeout(500);

    // Take screenshot with exceeded limit
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_exceeded_limit.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_exceeded_limit.png'));

    // Check if error class is applied and button is disabled
    const hasErrorClass = await page.$eval('#char-count', el => el.classList.contains('error'));
    const isButtonDisabled = await page.$eval('#post-thread', el => el.disabled);

    // Extract final character count
    const finalCount = await page.$eval('#char-count', el => el.textContent);

    return {
      success: true,
      action: "Checked thread character count functionality",
      extractedData: {
        initialCount,
        updatedCount,
        finalCount,
        hasWarningClass,
        hasErrorClass,
        isButtonDisabled
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to check thread character count",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

