import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the thread textarea to be visible
    await page.waitForSelector('#thread-text', { state: 'visible' });

    // Test 1: Check initial character count
    const initialCount = await page.textContent('#char-count');
    console.log(`Initial character count: ${initialCount}`);

    // Test 2: Type a short message and verify count
    await page.fill('#thread-text', 'Hello, this is a test thread!');
    await page.waitForTimeout(500); // Wait for character count update
    const shortCount = await page.textContent('#char-count');
    console.log(`Short message count: ${shortCount}`);

    // Test 3: Type a long message (over 500 characters)
    const longText = 'A'.repeat(600); // Create 600 character string
    await page.fill('#thread-text', longText);
    await page.waitForTimeout(500); // Wait for character count update
    const longCount = await page.textContent('#char-count');
    console.log(`Long message count: ${longCount}`);

    // Check if character count shows error styling when over limit
    const hasErrorClass = await page.evaluate(() => {
      const charCount = document.getElementById('char-count');
      return charCount.classList.contains('error');
    });

    // Check if post button is disabled when over limit
    const isPostDisabled = await page.evaluate(() => {
      const postBtn = document.getElementById('post-thread');
      return postBtn.disabled;
    });

    // Take screenshot of character count with long text
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_character_count.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_character_count.png'));

    // Clear the textarea
    await page.fill('#thread-text', '');
    await page.waitForTimeout(500); // Wait for character count update

    return {
      success: true,
      action: "Verified thread character count functionality",
      extractedData: {
        initialCount,
        shortCount,
        longCount,
        hasErrorClass,
        isPostDisabled
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete character count verification",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
