import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the thread textarea and character count to be visible
    await page.waitForSelector('#thread-text', { state: 'visible' });
    await page.waitForSelector('#char-count', { state: 'visible' });

    // 1. Check initial character count is 0/500
    const initialCharCount = await page.$eval('#char-count', el => el.textContent.trim());

    // 2. Type a short thread and check character count updates
    const shortText = 'Hello Threads!';
    await page.fill('#thread-text', shortText);

    // Wait for character count to update
    await page.waitForTimeout(200); // allow JS event to update
    const shortCharCount = await page.$eval('#char-count', el => el.textContent.trim());

    // Take screenshot after typing short thread
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_short_thread.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_short_thread.png'));

    // 3. Type a long thread (over 500 chars) and check character count and error class
    const longText = 'A'.repeat(510);
    await page.fill('#thread-text', longText);

    // Wait for character count to update and error class to apply
    await page.waitForTimeout(200);
    const longCharCount = await page.$eval('#char-count', el => el.textContent.trim());
    const charCountClass = await page.$eval('#char-count', el => el.className);

    // Take screenshot after typing long thread
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_long_thread.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_long_thread.png'));

    // 4. Check that the "Post" button is disabled when over 500 chars
    const postBtnDisabled = await page.$eval('#post-thread', el => el.disabled);

    // 5. Type a thread with 495 chars (warning state)
    const warningText = 'B'.repeat(495);
    await page.fill('#thread-text', warningText);
    await page.waitForTimeout(200);
    const warningCharCount = await page.$eval('#char-count', el => el.textContent.trim());
    const warningCharCountClass = await page.$eval('#char-count', el => el.className);

    // Take screenshot for warning state
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_warning_thread.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_warning_thread.png'));

    // 6. Clear the textarea for cleanliness
    await page.fill('#thread-text', '');

    return {
      success: true,
      action: "Checked thread character count: verified initial, normal, warning, and error states, and Post button disabling.",
      extractedData: {
        initialCharCount,
        shortCharCount,
        longCharCount,
        charCountClass,
        postBtnDisabled,
        warningCharCount,
        warningCharCountClass
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    const screenshots = [];
    // Initial screenshot of the loaded page
    const shot1 = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: shot1 });
    screenshots.push(shot1);

    // Wait for the compose thread textarea using a unique placeholder
    await page.waitForSelector('textarea[placeholder="Start a thread..."]', { timeout: 5000 });

    // Focus the textarea via placeholder (different selector than previous attempts)
    const textarea = await page.$('textarea[placeholder="Start a thread..."]');
    if (!textarea) throw new Error('Thread textarea not found');

    // Type a string of 501 characters to exceed the limit
    const longText = 'A'.repeat(501);
    await textarea.click();
    await textarea.fill(''); // clear first
    await textarea.type(longText, { delay: 1 });

    // Wait for the character count to update (using XPath for a different selector strategy)
    await page.waitForSelector('//*[@id="char-count" and contains(text(), "/500")]', { timeout: 3000 });

    // Screenshot after typing long text
    const shot2 = path.join(screenshotsDir, 'step_2_after_typing.png');
    await page.screenshot({ path: shot2 });
    screenshots.push(shot2);

    // Extract the character count text using XPath
    const charCountEl = await page.$('//span[@id="char-count"]');
    const charCountText = charCountEl ? (await charCountEl.textContent()).trim() : null;

    // Check if the post button is disabled (using aria-disabled or :disabled)
    // Use a different selector: button with text "Post" inside .compose-controls
    const postBtn = await page.$('.compose-controls >> text=Post');
    let postBtnDisabled = false;
    if (postBtn) {
      postBtnDisabled = await postBtn.isDisabled();
    }

    // Also check for error class on char-count (should be present if over limit)
    const charCountClass = charCountEl ? await charCountEl.getAttribute('class') : '';

    // Take a close-up screenshot of the controls area (use bounding box)
    const controls = await page.$('.compose-controls');
    if (controls) {
      const box = await controls.boundingBox();
      if (box) {
        const shot3 = path.join(screenshotsDir, 'step_3_controls_closeup.png');
        await page.screenshot({
          path: shot3,
          clip: {
            x: box.x,
            y: box.y,
            width: box.width,
            height: box.height
          }
        });
        screenshots.push(shot3);
      }
    }

    // Now type a valid short thread (under 500 chars)
    await textarea.fill('Short thread!');
    await page.waitForTimeout(300); // wait for char count update

    // Screenshot after valid input
    const shot4 = path.join(screenshotsDir, 'step_4_valid_input.png');
    await page.screenshot({ path: shot4 });
    screenshots.push(shot4);

    // Extract char count and post button state again
    const charCountTextValid = charCountEl ? (await charCountEl.textContent()).trim() : null;
    const postBtnDisabledValid = postBtn ? await postBtn.isDisabled() : null;
    const charCountClassValid = charCountEl ? await charCountEl.getAttribute('class') : '';

    return {
      success: true,
      action: "Checked thread character count: verified error state when exceeding 500 chars, and normal state under limit. Confirmed UI disables Post button and displays error styling.",
      extractedData: {
        charCountTextOverLimit: charCountText,
        charCountClassOverLimit: charCountClass,
        postBtnDisabledOverLimit: postBtnDisabled,
        charCountTextValid,
        charCountClassValid,
        postBtnDisabledValid
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    const screenshots = [];
    // Take initial screenshot of the loaded page
    const initialShot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialShot });
    screenshots.push(initialShot);

    // Wait for the textarea for composing a thread (using placeholder attribute as selector)
    await page.waitForSelector('textarea[placeholder="Start a thread..."]', { timeout: 5000 });

    // Type a test thread (using a string of 501 characters to test the character count and error)
    const testText = 'A'.repeat(501);
    await page.fill('textarea[placeholder="Start a thread..."]', testText);

    // Wait for the character count element to update (using the visible text content)
    await page.waitForSelector('span#char-count', { timeout: 3000 });

    // Take screenshot after typing (to show character count and any error state)
    const afterTypingShot = path.join(screenshotsDir, 'step_2_after_typing.png');
    await page.screenshot({ path: afterTypingShot });
    screenshots.push(afterTypingShot);

    // Extract the character count text and its CSS class for error/warning
    const charCountText = await page.$eval('span#char-count', el => el.textContent);
    const charCountClass = await page.$eval('span#char-count', el => el.className);

    // Check if the Post button is disabled (using its ID)
    const postBtnDisabled = await page.$eval('#post-thread', el => el.disabled);

    // Now, type a valid thread (e.g., 100 characters)
    const validText = 'B'.repeat(100);
    await page.fill('textarea[placeholder="Start a thread..."]', validText);

    // Wait for the character count to update
    await page.waitForFunction(
      () => document.querySelector('span#char-count').textContent.startsWith('100'),
      { timeout: 2000 }
    );

    // Take screenshot after valid input
    const afterValidShot = path.join(screenshotsDir, 'step_3_after_valid.png');
    await page.screenshot({ path: afterValidShot });
    screenshots.push(afterValidShot);

    // Extract the updated character count and class
    const charCountTextValid = await page.$eval('span#char-count', el => el.textContent);
    const charCountClassValid = await page.$eval('span#char-count', el => el.className);

    // Check if the Post button is enabled now
    const postBtnEnabled = !(await page.$eval('#post-thread', el => el.disabled));

    return {
      success: true,
      action: "Checked thread character count, verified error state for >500 chars and normal state for valid input.",
      extractedData: {
        overLimit: {
          charCountText,
          charCountClass,
          postBtnDisabled
        },
        validInput: {
          charCountText: charCountTextValid,
          charCountClass: charCountClassValid,
          postBtnEnabled
        }
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
