import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the sidebar nav to be visible
    await page.waitForSelector('.sidebar .nav-menu');

    // Find the "Bookmarks" nav item using multiple strategies
    // 1. By data-view attribute
    const bookmarksNavSelector = '.nav-item[data-view="bookmarks"]';
    await page.waitForSelector(bookmarksNavSelector);

    // 2. Double-check with text content as fallback (robustness)
    // (not strictly necessary here, but per instructions)
    // const bookmarksNavByText = await page.$(`xpath=//li[contains(@class,"nav-item") and .//span[text()[normalize-space(.)="Bookmarks"]]]`);

    // Click the Bookmarks nav item
    await page.click(bookmarksNavSelector);

    // Wait for the bookmarks view to become active
    await page.waitForSelector('#bookmarks-view.view.active', { timeout: 3000 });

    // Wait for the bookmarks feed to be visible
    await page.waitForSelector('#bookmarks-feed');

    // Take screenshot after navigation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_bookmarks_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_bookmarks_view.png'));

    // Extract data to verify navigation: Check for the Bookmarks header and list of threads/bookmarks
    const bookmarksHeader = await page.textContent('#bookmarks-view .content-header h2');
    // Extract the number of thread items in the bookmarks feed
    const bookmarkThreadsCount = await page.$$eval(
      '#bookmarks-feed .thread-item',
      els => els.length
    );
    // Also, check for the empty state (if no bookmarks)
    const emptyStateExists = await page.$('#bookmarks-feed .empty-state') !== null;

    // Compose extracted data
    const extractedData = {
      bookmarksHeader: bookmarksHeader ? bookmarksHeader.trim() : null,
      bookmarkThreadsCount,
      emptyState: emptyStateExists
    };

    return {
      success: true,
      action: "Navigated to the Bookmarks section via sidebar and verified bookmarks view is active.",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Step 1: Initial screenshot
    const initialShot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialShot });
    screenshots.push(initialShot);

    // Step 2: Wait for the sidebar nav-menu to be visible
    await page.waitForSelector('nav.sidebar ul.nav-menu', { timeout: 5000 });

    // Step 3: Find the "Bookmarks" nav item using its text content (robust to class changes)
    // Use XPath for text matching, ensuring we do NOT rely on previous selectors
    const [bookmarksNavItem] = await page.$x("//nav[contains(@class,'sidebar')]//ul[contains(@class,'nav-menu')]/li[./span[normalize-space(text())='Bookmarks']]");
    if (!bookmarksNavItem) {
      throw new Error('Bookmarks nav item not found');
    }

    // Step 4: Scroll into view and click the Bookmarks nav item
    await bookmarksNavItem.scrollIntoViewIfNeeded();
    await bookmarksNavItem.click();

    // Step 5: Wait for the Bookmarks view to be active and visible
    // Use a selector based on the h2 header text inside the main content area
    await page.waitForSelector("main.main-content .view.active h2", { timeout: 5000 });
    // Confirm it's the Bookmarks view by checking the header text
    const bookmarksHeader = await page.$("main.main-content .view.active h2");
    const headerText = await bookmarksHeader.evaluate(el => el.textContent.trim());
    if (headerText !== 'Bookmarks') {
      throw new Error('Bookmarks view did not become active');
    }

    // Step 6: Wait for bookmarks feed to be loaded (either empty state or threads)
    // Use a selector for the bookmarks-feed container
    await page.waitForSelector("#bookmarks-feed", { timeout: 5000 });

    // Step 7: Take a screenshot of the Bookmarks view
    const bookmarksShot = path.join(screenshotsDir, 'step_2_bookmarks_view.png');
    await page.screenshot({ path: bookmarksShot });
    screenshots.push(bookmarksShot);

    // Step 8: Extract data from the Bookmarks view for verification
    // Try to extract thread content if present, or empty state message
    const bookmarksContent = await page.evaluate(() => {
      const feed = document.querySelector('#bookmarks-feed');
      if (!feed) return { type: 'error', message: 'No bookmarks feed found' };

      // Check for empty state
      const emptyState = feed.querySelector('.empty-state h3');
      if (emptyState) {
        return { type: 'empty', message: emptyState.textContent.trim() };
      }

      // Otherwise, extract up to 3 thread contents
      const threads = Array.from(feed.querySelectorAll('.thread-item'))
        .slice(0, 3)
        .map(item => {
          const content = item.querySelector('.thread-text');
          return content ? content.textContent.trim() : '';
        });
      return { type: 'threads', threads };
    });

    return {
      success: true,
      action: "Navigated to Bookmarks via sidebar and verified Bookmarks view is active",
      extractedData: bookmarksContent,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const initialShot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialShot });
    screenshots.push(initialShot);

    // Wait for the sidebar nav-menu to be visible
    await page.waitForSelector('nav.sidebar ul.nav-menu', { timeout: 5000 });

    // Find the Bookmarks nav item using its icon and text (robust selector)
    // Use XPath to find the <li> with a child <span> containing "Bookmarks"
    const [bookmarksNavItem] = await page.$x(
      "//nav[contains(@class,'sidebar')]//ul[contains(@class,'nav-menu')]/li[.//span[text()='Bookmarks']]"
    );
    if (!bookmarksNavItem) {
      throw new Error('Bookmarks navigation item not found');
    }

    // Scroll into view and click Bookmarks
    await bookmarksNavItem.scrollIntoViewIfNeeded();
    await bookmarksNavItem.click();

    // Wait for the bookmarks view to become active
    // The bookmarks view has id="bookmarks-view" and class "view active"
    await page.waitForSelector('#bookmarks-view.view.active', { timeout: 5000 });

    // Wait for bookmarks-feed to be visible
    await page.waitForSelector('#bookmarks-feed', { timeout: 3000 });

    // Take screenshot after navigation
    const afterNavShot = path.join(screenshotsDir, 'step_2_bookmarks_view.png');
    await page.screenshot({ path: afterNavShot });
    screenshots.push(afterNavShot);

    // Extract data from the bookmarks view
    // Try to get all thread items in the bookmarks feed
    const bookmarkedThreads = await page.$$eval(
      '#bookmarks-feed .thread-item',
      nodes => nodes.map(node => {
        const username = node.querySelector('.thread-username')?.textContent?.trim() || '';
        const content = node.querySelector('.thread-text')?.textContent?.trim() || '';
        return { username, content };
      })
    );

    // If no threads, check for the empty state message
    let emptyState = null;
    if (!bookmarkedThreads.length) {
      emptyState = await page.$eval(
        '#bookmarks-feed .empty-state h3',
        el => el.textContent.trim()
      ).catch(() => null);
    }

    return {
      success: true,
      action: "Navigated to Bookmarks via sidebar and verified the Bookmarks view is active.",
      extractedData: {
        bookmarksCount: bookmarkedThreads.length,
        bookmarks: bookmarkedThreads,
        emptyState
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
