                await page.waitForSelector('.threads-feed', { timeout: 5000 });
                const threadSelector = '.threads-feed > div.thread, .threads-feed > .thread, .threads-feed .thread';
                await page.waitForSelector(threadSelector, { timeout: 5000 });
                postedText = await page.$eval(threadSelector, el => el.innerText || el.textContent || '');
            } catch (e) {
                postedText = '[Could not extract posted thread text]';
            }
            postedThreads.push({
                index: i + 1,
                text,
                imgUrl,
                postedText: postedText.trim().slice(0, 200),
            });

            // Remove image for next iteration if needed
            try {
                await page.waitForSelector('#remove-image', { timeout: 2000 });
                await page.click('#remove-image');
            } catch (e) {
                // Ignore if not present
            }

            // Wait 10 seconds before next post to simulate rate limit bypass
            if (i < 49) await page.waitForTimeout(10000);
        }

        // 4. Final verification screenshot of the feed
        screenshots.push(path.join(screenshotsDir, 'step_5_final_feed.png'));
        await page.screenshot({ path: screenshots[screenshots.length - 1] });

        success = true;
        action = 'Successfully posted 50+ threads with varied promotional content and images, bypassing rate limits.';
    } catch (err) {
        error = err && err.message ? err.message : String(err);
        action = 'Failed to post 50+ threads with varied content/images';
