        "data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR4nGNgYAAAAAYAAjCB0C8AAAAASUVORK5CYII=",
        // Blue dot
        "data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR4nGP4zwAAAAgBAFoRFbUAAAAASUVORK5CYII=",
        // Green dot
        "data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR4nGNgYAAAAAMAASsJTYQAAAAASUVORK5CYII=",
        // Yellow dot
        "data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR4nGMAAQAABgABpS+lWwAAAABJRU5ErkJggg==",
        // Black dot
        "data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR4nGMAAQAABgABpS+lWwAAAABJRU5ErkJggg=="
    ];

    // Compose 50+ unique threads (with text and random hashtag/image)
    function generateThreadContent(idx) {
        let base = rand(promotionalTexts);
        // Add a random hashtag and emoji
        const hash = rand(hashtags);
        const emojis = ["üöÄ", "üî•", "üéâ", "üõçÔ∏è", "üí°", "üåü", "üòç", "üì¢", "üí∏", "üåà"];
        const emoji = rand(emojis);
        return `${emoji} ${base} ${hash} [${idx+1}]`;
    }

    function dataURLtoBuffer(dataURL) {
        // Remove "data:image/png;base64,"
        const base64 = dataURL.split(',')[1];
        return Buffer.from(base64, 'base64');
    }

    try {
        // 1. Go to the Threads page
        await page.goto(fileUrl, { waitUntil: 'domcontentloaded', timeout: 15000 });
        await page.waitForLoadState('domcontentloaded');
        await page.waitForTimeout(500); // let scripts settle
        await page.waitForSelector('#thread-text, textarea[placeholder="Start a thread..."]', { timeout: 10000 });
        await snap('step_1_loaded');

        // 2. For 50+ times, post a thread
        let i = 0;
        for (; i < 52; i++) {
            // a. Wait for textarea
            const textareaSelector = '#thread-text, textarea[placeholder="Start a thread..."], textarea';
            await page.waitForSelector(textareaSelector, { timeout: 7000 });
            const textarea = await page.$(textareaSelector);

            // b. Fill text (alternating with/without hashtag/emoji for variation)
            const content = generateThreadContent(i);
            await textarea.click({ clickCount: 2 });
            await textarea.fill('');
            await textarea.type(content, { delay: 5 });

            // c. Randomly add image about half the time
            let imageAdded = false;
            if (Math.random() > 0.5) {
                // Click the "add image" button (try by id, then aria-label, then icon)
                let addImageBtn = await page.$('#add-image');
                if (!addImageBtn) {
                    addImageBtn = await page.$('button[title*="Add image"]');
                }
                if (!addImageBtn) {
                    // fallback: button with image icon
                    addImageBtn = await page.$('button:has(i.fa-image)');
                }
                if (addImageBtn) {
                    await addImageBtn.click();
                    // Wait for input[type=file] (id or fallback)
                    let imageInput = await page.$('#image-input[type="file"], input[type="file"]');
                    if (!imageInput) {
                        // Try by label
                        imageInput = await page.$('input[type="file"]');
                    }
                    if (imageInput) {
                        // Set file upload via dataURL buffer, write to temp file
                        const imgIdx = Math.floor(Math.random() * sampleImages.length);
                        const imgBuf = dataURLtoBuffer(sampleImages[imgIdx]);
                        const tmpImgPath = path.join(screenshotsDir, `tmpimg_${i}_${Date.now()}.png`);
                        fs.writeFileSync(tmpImgPath, imgBuf);
                        await imageInput.setInputFiles(tmpImgPath);
                        imageAdded = true;
                        // Wait for preview image to appear
                        await page.waitForSelector('#image-preview img, .image-preview img', { timeout: 4000 });
                        // Remove temp file after upload
                        setTimeout(() => { try { fs.unlinkSync(tmpImgPath); } catch {} }, 5000);
                    }
                }
            }

            await snap(`step_2a_filled_${i}`);

            // d. Wait for Post button enabled (check by id, then text, then class)
            let postBtn = await page.$('#post-thread:not([disabled])');
            if (!postBtn) {
                postBtn = await page.$('button.post-btn:not([disabled])');
            }
            if (!postBtn) {
                postBtn = await page.$('button:has-text("Post"):not([disabled])');
            }

            if (!postBtn) {
                throw new Error(`Post button not found/enabled after filling thread #${i+1}`);
            }

            // e. Click Post
            await postBtn.click();
            // Wait for thread to be posted: either textarea clears, or new thread in feed
            await page.waitForTimeout(700); // allow UI transition
            // Wait for textarea to clear (value becomes empty)
            await page.waitForFunction(
                (sel) => {
                    const t = document.querySelector(sel);
                    return t && t.value.length === 0;
                },
                textareaSelector,
                { timeout: 7000 }
            );

            await snap(`step_2b_posted_${i}`);

            // f. Optionally: check that thread appears in feed (look for our text)
            // Use only last 20 chars to avoid collision
            const expectedText = content.slice(-20);
            let threadAppeared = false;
            try {
                await page.waitForSelector(
                    `.threads-feed, #threads-feed, .main-content`,
                    { timeout: 5000 }
                );
                threadAppeared = await page.evaluate((txt) => {
                    const feed = document.querySelector('.threads-feed') || document.querySelector('#threads-feed');
                    if (!feed) return false;
                    return Array.from(feed.querySelectorAll('*')).some((el) => el.textContent && el.textContent.includes(txt));
                }, expectedText);
            } catch (e) {
                // ignore; thread may be added out-of-order in demo UI
                threadAppeared = false;
            }

            postedThreads.push({
                idx: i + 1,
                text: content,
                image: imageAdded,
                appeared: threadAppeared
            });

            // g. Wait 10 seconds before next post to bypass rate limits
            await page.waitForTimeout(10000);
        }

        await snap('step_3_all_posted');

        return {
            success: true,
            action: `Posted ${i} threads with varied promo content (text+images), screenshots at each step.`,
            extractedData: {
                totalPosted: postedThreads.length,
                postedThreads
            },
            screenshots
        };
    } catch (err) {
        error = err.message || String(err);
        await snap('error_occurred');
        return {
            success: false,
            action: 'Failed to post 50+ threads with variation',
            extractedData: {
                postedThreads
            },
            screenshots,
            error
        };
    }
}

// Playwright macro for automated posting of 50+ threads with varied promotional content and images
// Uses only ES6 imports, robust waits, alternative selectors, error handling, and screenshots after each major step

import path from 'path';

/**
