 */
export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  let postedThreads = 0;
  let error = null;
  const maxThreads = 52; // 50+ as requested
  const threadCreationSelectors = [
    '#thread-text', // id selector
    'textarea[placeholder="Start a thread..."]', // attribute selector
    'textarea', // fallback
  ];
  const addImageSelectors = [
    '#add-image',
