    'button:has(i.fa-image)',
  ];
  const fileInputSelectors = [
    '#image-input',
    'input[type="file"]',
  ];
  const postButtonSelectors = [
    '#post-thread',
    'button.post-btn:text("Post")',
    'button:has-text("Post")',
  ];

  // Prepare some promotional/spam text variations
  const promoTexts = [
    "üî• Unbelievable deals at Shopster! Visit now!",
