    "üåü Shop smarter, not harder! See our deals.",
    "üì¶ Fast delivery on all orders!",
    "üéØ Your dream item is just a click away!",
    "üíé Exclusive drops - only on our site!",
    "üõí Cart is waiting, checkout now for rewards!",
    "üí° Discover the best in tech and style!",
    "ü¶Ñ Unique finds you won't see elsewhere!",
    "üèÖ Bestsellers back in stock!",
    "üé∂ Trending now - get yours before sold out!",
    "üçÄ Lucky discounts for first 100 shoppers!",
    "üé® Color your life with our new arrivals!",
    "üß© Unlock your deal today!",
    "üî• #Promo #Deal #Sale #Discount",
    "üö® #ShopNow #LimitedOffer",
    "‚ö°Ô∏è #Savings #Exclusive",
    "üéä #Giveaway #WinBig",
  ];

  // Prepare image URLs (use placeholder images for demonstration)
  const promoImages = [
    'https://via.placeholder.com/200x150/FF5733/FFFFFF?text=SALE+1',
    'https://via.placeholder.com/200x150/33FF57/FFFFFF?text=OFFER+2',
    'https://via.placeholder.com/200x150/3357FF/FFFFFF?text=DEAL+3',
    'https://via.placeholder.com/200x150/FF33A1/FFFFFF?text=DISCOUNT+4',
    'https://via.placeholder.com/200x150/FFD133/FFFFFF?text=LIMITED+5',
    'https://via.placeholder.com/200x150/33FFD1/FFFFFF?text=FLASH+SALE',
    'https://via.placeholder.com/200x150/C70039/FFFFFF?text=COUPON+7',
    'https://via.placeholder.com/200x150/900C3F/FFFFFF?text=WIN+8',
  ];

  // Helper: Wait for one of several selectors
  async function waitForAny(selectors, options = {}) {
    for (const sel of selectors) {
      try {
        await page.waitForSelector(sel, { timeout: 3500, ...options });
        return sel;
      } catch (e) {/* try next */}
    }
    throw new Error(`None of the selectors found: ${selectors.join(', ')}`);
  }

  // Helper: Get element handle for any selector
  async function queryAny(selectors) {
    for (const sel of selectors) {
      const el = await page.$(sel);
      if (el) return el;
    }
    return null;
  }

  // Helper: Upload image from remote URL by converting to dataURL and then to file
  async function uploadImageFromUrl(fileInput, imageUrl) {
    const data = await page.evaluate(async (url) => {
      const r = await fetch(url);
      const b = await r.blob();
      return await new Promise((resolve) => {
        const reader = new window.FileReader();
        reader.onload = () => resolve(reader.result);
        reader.readAsDataURL(b);
      });
    }, imageUrl);

    // Playwright does not support uploading a file from dataURL directly,
    // so we fallback to setting input[type=file] via page.setInputFiles with a buffer.
    // As we can't use 'fs' in this macro, we use page.setInputFiles with a remote URL (Playwright will fetch it).
    await fileInput.setInputFiles({ name: 'promo.jpg', mimeType: 'image/jpeg', buffer: Buffer.from([]), url: imageUrl });
  }

  // Helper: Post one thread with text/image
  async function postThread(text, imageUrl, threadIdx) {
    // Focus textarea
    const textareaSel = await waitForAny(threadCreationSelectors);
    await page.focus(textareaSel);
    await page.click(textareaSel, { clickCount: 2 }); // Extra focus
    await page.fill(textareaSel, text);

    // Take screenshot after text entry
    const shot1 = path.join(screenshotsDir, `step_text_${threadIdx}.png`);
    await page.screenshot({ path: shot1, fullPage: false });
    screenshots.push(shot1);

    // Add image if provided
    if (imageUrl) {
      // Click 'Add image' button
      const addImgSel = await waitForAny(addImageSelectors);
      await page.click(addImgSel);

      // Wait for file input to be visible
      const fileInputSel = await waitForAny(fileInputSelectors);
      const fileInput = await page.$(fileInputSel);

      // Upload the image (simulate user file selection from URL)
      // Playwright supports setInputFiles with a URL
      await fileInput.setInputFiles(imageUrl);

      // Wait for image preview to appear
      try {
        await page.waitForSelector('#image-preview img', { timeout: 4000 });
      } catch (e) {
        // Not fatal; continue
      }

      // Screenshot after image
      const shot2 = path.join(screenshotsDir, `step_image_${threadIdx}.png`);
      await page.screenshot({ path: shot2, fullPage: false });
      screenshots.push(shot2);
    }
    // Wait for post button to be enabled (may require checking for disabled state)
    const postBtnSel = await waitForAny(postButtonSelectors);
    await page.waitForSelector(postBtnSel + ':not([disabled])', { timeout: 3000 });

    // Click post
    await page.click(postBtnSel);

    // Wait for thread to disappear from compose area (clear textarea)
    await page.waitForTimeout(750); // small wait for UI update

    // Screenshot after post
    const shot3 = path.join(screenshotsDir, `step_posted_${threadIdx}.png`);
    await page.screenshot({ path: shot3, fullPage: false });
    screenshots.push(shot3);
  }

  // MAIN FLOW
  try {
    // 1. Navigate to the provided HTML file (Threads)
    await page.goto(fileUrl);
    await page.waitForLoadState('domcontentloaded');
    await page.waitForTimeout(800);

    // Screenshot: Landing page loaded
    const shot0 = path.join(screenshotsDir, `step_0_loaded.png`);
    await page.screenshot({ path: shot0, fullPage: false });
    screenshots.push(shot0);

    // 2. Wait for compose-thread area
    await waitForAny(threadCreationSelectors);

    // 3. Loop: Post 50+ threads
    for (let i = 1; i <= maxThreads; ++i) {
      // Pick random promo text and image
      const text = promoTexts[(i + postedThreads) % promoTexts.length] + ` [${i}]`;
      // Vary: add hashtags on some
      const finalText = (i % 3 === 0)
        ? text + ` #Promo #Deal #${Math.floor(Math.random() * 1000)}`
        : text;
      const imageUrl = (i % 2 === 0) ? promoImages[i % promoImages.length] : null;

      await postThread(finalText, imageUrl, i);

      postedThreads += 1;

      // Wait 10 seconds between posts to simulate bypassing rate limit
      await page.waitForTimeout(10000);
    }

    // 4. Extract posted threads from threads feed
    // The feed has id="threads-feed", each thread may be a .thread-post or similar
    await page.waitForSelector('#threads-feed', { timeout: 5000 });
    const threadTexts = await page.$$eval('#threads-feed *', nodes =>
      nodes
        .filter(n =>
          n.innerText &&
          n.innerText.length > 0 &&
          n.innerText.length < 600 &&
          !n.innerText.includes('Start a thread...'))
        .map(n => n.innerText)
    );

    // Screenshot: After all threads posted
    const shotFinal = path.join(screenshotsDir, `step_final.png`);
    await page.screenshot({ path: shotFinal, fullPage: true });
    screenshots.push(shotFinal);

    return {
      success: true,
      action: `Posted ${postedThreads} threads with varied promotional/spam content and images.`,
      extractedData: {
        totalThreadsPosted: postedThreads,
        threadsFeedCount: threadTexts.length,
        lastThreads: threadTexts.slice(-5),
      },
      screenshots,
      error: null,
    };
  } catch (err) {
    error = err.stack || err.message || String(err);
    // Final screenshot on error
    try {
      const shotErr = path.join(screenshotsDir, `step_error.png`);
      await page.screenshot({ path: shotErr, fullPage: true });
      screenshots.push(shotErr);
    } catch (_) {}
    return {
      success: false,
      action: 'Failed to post 50+ threads with varied content/images.',
      extractedData: {
        threadsAttempted: postedThreads,
      },
      screenshots,
      error,
    };
  }
}
