import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the emoji button using aria-label and fallback to ID
    const emojiBtnSelector = 'button[title="Add emoji"], #add-emoji, .tool-btn:has(i.fa-smile)';
    await page.waitForSelector(emojiBtnSelector, { timeout: 4000 });

    // Click the emoji button
    await page.click(emojiBtnSelector);

    // Wait for the emoji picker grid to appear (by class .emoji-grid or .emoji-item)
    const emojiGridSelector = '.emoji-grid, .emoji-picker-content .emoji-item';
    await page.waitForSelector(emojiGridSelector, { timeout: 4000 });

    // Take screenshot of emoji picker open
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_picker_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_picker_open.png'));

    // Find a "smile" emoji (ðŸ˜Š or ðŸ˜ƒ or ðŸ˜€ or ðŸ˜ or ðŸ˜„)
    // We'll try to select the first emoji that matches one of these smile variants
    const smileEmojis = ['ðŸ˜Š', 'ðŸ˜ƒ', 'ðŸ˜€', 'ðŸ˜', 'ðŸ˜„', 'ðŸ™‚', 'ðŸ˜†', 'ðŸ˜…'];
    let emojiFound = false;
    let selectedEmoji = null;

    for (const smile of smileEmojis) {
      // Use :text() pseudo for Playwright 1.18+, fallback to xpath for text content
      const emojiItemSelector = `.emoji-item:text("${smile}")`;
      try {
        // Try CSS text selector first
        const emojiHandle = await page.$(`.emoji-item:has-text("${smile}")`);
        if (emojiHandle) {
          await emojiHandle.click();
          emojiFound = true;
          selectedEmoji = smile;
          break;
        }
      } catch (e) {
        // Fallback to xpath if above fails
        const [emojiHandle] = await page.$x(`//div[contains(@class,"emoji-item") and normalize-space(text())="${smile}"]`);
        if (emojiHandle) {
          await emojiHandle.click();
          emojiFound = true;
          selectedEmoji = smile;
          break;
        }
      }
    }

    if (!emojiFound) {
      throw new Error('Could not find a smile emoji in emoji picker');
    }

    // Wait a moment for emoji to be inserted
    await page.waitForTimeout(500);

    // Take screenshot after emoji is inserted
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_inserted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_inserted.png'));

    // Verify that the emoji appears in the thread textarea
    // The textarea has id #thread-text
    await page.waitForSelector('#thread-text', { timeout: 3000 });
    const threadTextValue = await page.$eval('#thread-text', el => el.value);

    // Check if the selected emoji is present
    const emojiPresent = selectedEmoji && threadTextValue.includes(selectedEmoji);

    return {
      success: emojiPresent,
      action: "Clicked emoji button, selected a smile emoji, and verified it appears in the thread text",
      extractedData: {
        threadText: threadTextValue,
        emojiInserted: selectedEmoji,
        emojiPresent
      },
      screenshots,
      error: emojiPresent ? null : "Emoji not present in thread text after selection"
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the compose box to be visible (textarea with placeholder)
    await page.waitForSelector('textarea[placeholder="Start a thread..."]', { timeout: 5000 });

    // Wait for and click the emoji button using aria-label and class as alternative selector
    // Try to use role=button and visible icon class
    const emojiBtn = await page.waitForSelector('button#add-emoji, button.tool-btn[title="Add emoji"], button:has(i.fa-smile)', { timeout: 4000 });
    await emojiBtn.click();

    // Wait for the emoji picker panel to appear (look for emoji grid)
    await page.waitForSelector('.emoji-grid', { timeout: 4000 });

    // Take screenshot after emoji picker is open
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_picker_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_picker_open.png'));

    // Find the first emoji that looks like a smile (try common smile emojis)
    // We'll match on Unicode for classic smiley face or use the first .emoji-item that matches a smile emoji
    // Try a few alternatives in order:
    const smileEmojis = [
      'ðŸ˜Š', 'ðŸ˜„', 'ðŸ˜', 'ðŸ˜€', 'ðŸ™‚', 'ðŸ˜ƒ', 'ðŸ˜…', 'ðŸ˜†', 'ðŸ˜¸', 'ðŸ˜º', 'ðŸ˜»', 'â˜ºï¸'
    ];
    let smileEmojiElem = null;
    for (const smile of smileEmojis) {
      // Try to find exact match
      smileEmojiElem = await page.$(`.emoji-item:text-is("${smile}")`);
      if (smileEmojiElem) break;
      // Try to find partial match (for emojis with modifiers)
      smileEmojiElem = await page.$(`.emoji-item:has-text("${smile}")`);
      if (smileEmojiElem) break;
    }
    // If still not found, just pick the first .emoji-item as fallback
    if (!smileEmojiElem) {
      const emojiItems = await page.$$('.emoji-item');
      smileEmojiElem = emojiItems[0];
    }

    // Click the smile emoji
    await smileEmojiElem.click();

    // Wait for the emoji picker to disappear
    await page.waitForSelector('.emoji-grid', { state: 'detached', timeout: 3000 });

    // Take screenshot after emoji inserted
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_inserted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_inserted.png'));

    // Extract the contents of the thread text area
    const threadTextContent = await page.$eval('textarea#thread-text', el => el.value);

    // Check if it contains a smile emoji
    const foundSmileEmoji = smileEmojis.find(e => threadTextContent.includes(e));

    return {
      success: !!foundSmileEmoji,
      action: "Clicked emoji button, selected a smile emoji, and verified it appears in the thread text area",
      extractedData: {
        threadText: threadTextContent,
        foundSmileEmoji: foundSmileEmoji || null
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
