import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the compose thread textarea (with placeholder)
    const threadTextareaSelector = 'textarea[placeholder="Start a thread..."]';
    await page.waitForSelector(threadTextareaSelector, { state: 'visible', timeout: 5000 });

    // Wait for the emoji button using aria-label and icon as fallback
    let emojiBtn = await page.$('button#add-emoji');
    if (!emojiBtn) {
      // Fallback: Find by button with smile icon
      emojiBtn = await page.$('button:has(i.fa-smile)');
    }
    if (!emojiBtn) {
      // Fallback: Find by title attribute
      emojiBtn = await page.$('button[title="Add emoji"]');
    }
    if (!emojiBtn) throw new Error('Emoji button not found');

    // Scroll into view and click the emoji button
    await emojiBtn.scrollIntoViewIfNeeded();
    await emojiBtn.click();

    // Wait for the emoji picker to appear (wait for one emoji-item)
    await page.waitForSelector('.emoji-item', { state: 'visible', timeout: 4000 });

    // Find a SMILE emoji (use common smileys) - try various strategies
    // Try to pick a smile emoji by its unicode/text
    const smileEmojis = [
      'ðŸ˜Š', 'ðŸ˜ƒ', 'ðŸ˜„', 'ðŸ˜', 'ðŸ™‚', 'ðŸ˜†', 'ðŸ˜…', 'ðŸ˜€', 'ðŸ˜¸', 'ðŸ˜º'
    ];
    let found = false;
    let smileEmojiItem = null;
    for (const smile of smileEmojis) {
      smileEmojiItem = await page.$(`.emoji-item:text-is("${smile}")`);
      if (smileEmojiItem) {
        found = true;
        break;
      }
    }
    if (!found) {
      // Fallback: get first emoji-item and use its value
      smileEmojiItem = (await page.$$('.emoji-item'))[0];
      if (!smileEmojiItem) throw new Error('No emoji items found');
    }

    // Get the emoji character for verification before clicking
    const smileEmojiChar = await smileEmojiItem.evaluate(el => el.textContent.trim());

    // Click the selected emoji
    await smileEmojiItem.click();

    // Wait for the emoji picker to close (emoji-item disappears)
    await page.waitForSelector('.emoji-item', { state: 'hidden', timeout: 4000 });

    // Take screenshot after emoji insert
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_selected.png'));

    // Wait for textarea content to update
    await page.waitForTimeout(500); // Give time for input to update

    // Extract the thread textarea value
    const threadText = await page.$eval(threadTextareaSelector, el => el.value);

    // Check if the smile emoji appears in the thread text
    const emojiPresent = threadText.includes(smileEmojiChar);

    // Take screenshot of completed state
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_in_text.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_in_text.png'));

    return {
      success: emojiPresent,
      action: emojiPresent
        ? `Clicked emoji button, selected '${smileEmojiChar}' emoji, and verified it appears in thread text`
        : `Emoji '${smileEmojiChar}' was not found in the thread text`,
      extractedData: {
        insertedEmoji: smileEmojiChar,
        threadText,
        emojiPresent
      },
      screenshots,
      error: emojiPresent ? null : `Emoji '${smileEmojiChar}' not present in thread textarea`
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the emoji button by icon (fallback in case #add-emoji changes)
    const emojiBtnSelector = 'button.tool-btn#add-emoji, button[title="Add emoji"], .tool-btn:has(i.fa-smile)';
    await page.waitForSelector(emojiBtnSelector, { timeout: 3000 });

    // Screenshot before emoji picker
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_emoji_picker.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_emoji_picker.png'));

    // Click emoji button via icon fallback for robustness
    const emojiBtns = await page.$$(emojiBtnSelector);
    if (!emojiBtns.length) throw new Error('Emoji button not found');
    await emojiBtns[0].click();

    // Wait for emoji picker grid to appear
    await page.waitForSelector('.emoji-grid', { timeout: 3000 });

    // Screenshot of emoji picker open
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_picker_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_picker_open.png'));

    // Select a "smile" emoji from the grid using visible text or Unicode (robust selection)
    const smileEmojis = ['ðŸ˜ƒ', 'ðŸ˜„', 'ðŸ˜Š', 'ðŸ™‚', 'ðŸ˜', 'ðŸ˜€', 'ðŸ˜†', 'ðŸ˜…', 'ðŸ˜¸', 'ðŸ˜º', 'ðŸ˜ƒ', 'ðŸ˜‹', 'ðŸ˜Ž', 'ðŸ˜‡', 'ðŸ˜'];
    let smileEmojiHandle = null;
    for (let emoji of smileEmojis) {
      // Try to find by emoji content
      smileEmojiHandle = await page.$(`.emoji-grid .emoji-item:text("${emoji}")`);
      if (!smileEmojiHandle) {
        // Fallback: use XPath contains text
        const [el] = await page.$x(`//div[contains(@class,"emoji-item") and normalize-space(text())='${emoji}']`);
        if (el) smileEmojiHandle = el;
      }
      if (smileEmojiHandle) break;
    }
    if (!smileEmojiHandle) {
      // As fallback, click the first emoji in the grid
      smileEmojiHandle = await page.$('.emoji-grid .emoji-item');
    }
    if (!smileEmojiHandle) throw new Error('Smile emoji not found in the emoji picker');
    const smileEmojiText = await smileEmojiHandle.evaluate(node => node.textContent.trim());
    await smileEmojiHandle.click();

    // Wait for emoji picker to disappear and the textarea to update
    await page.waitForTimeout(400); // Short delay for UI update

    // Screenshot after emoji selection
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_emoji_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_emoji_selected.png'));

    // Extract the thread text area value
    const textAreaSelector = '#thread-text, textarea[placeholder="Start a thread..."]';
    await page.waitForSelector(textAreaSelector, { timeout: 2000 });
    const threadText = await page.$eval(textAreaSelector, el => el.value);

    // Check if the selected smile emoji is present in the textarea
    const emojiInserted = smileEmojis.some(e => threadText.includes(e)) || threadText.includes(smileEmojiText);

    return {
      success: emojiInserted,
      action: "Clicked emoji button, selected a smile emoji, and verified it appears in the thread text",
      extractedData: {
        emojiInserted,
        threadText,
        smileEmoji: smileEmojiText
      },
      screenshots,
      error: emojiInserted ? null : "Smile emoji was not inserted into the thread text"
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
