import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the emoji button (ID: #add-emoji) to be visible and click it
    await page.waitForSelector('#add-emoji', { state: 'visible', timeout: 5000 });
    await page.click('#add-emoji');

    // Wait for emoji picker to appear (look for .emoji-item)
    await page.waitForSelector('.emoji-item', { state: 'visible', timeout: 5000 });

    // Take screenshot of emoji picker open
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_picker_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_picker_open.png'));

    // Find a smile emoji in the picker
    // We'll look for a .emoji-item whose textContent includes a smile emoji
    // Common smile emojis: ðŸ˜€ ðŸ˜ƒ ðŸ˜„ ðŸ˜ ðŸ˜† ðŸ˜Š ðŸ™‚ ðŸ˜… ðŸ˜ ðŸ˜ ðŸ˜ƒ ðŸ˜„ ðŸ˜ ðŸ˜† ðŸ˜Š ðŸ™‚ ðŸ˜…
    // We'll try to click the first .emoji-item that matches a smile emoji
    const smileEmojis = ['ðŸ˜€','ðŸ˜ƒ','ðŸ˜„','ðŸ˜','ðŸ˜†','ðŸ˜Š','ðŸ™‚','ðŸ˜…','ðŸ˜¸','ðŸ˜º'];
    const emojiHandles = await page.$$('.emoji-item');
    let smileEmojiText = null;
    let found = false;
    for (const handle of emojiHandles) {
      const text = await handle.textContent();
      if (smileEmojis.some(e => text && text.includes(e))) {
        smileEmojiText = text.trim();
        await handle.click();
        found = true;
        break;
      }
    }
    if (!found) {
      throw new Error('No smile emoji found in emoji picker');
    }

    // Wait for the emoji picker to disappear (optional, but safer)
    await page.waitForSelector('.emoji-item', { state: 'detached', timeout: 3000 }).catch(() => {});

