    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_inserted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_inserted.png'));

    // Extract the value of the thread textarea (#thread-text)
    await page.waitForSelector('#thread-text', { state: 'visible', timeout: 3000 });
    const threadText = await page.$eval('#thread-text', el => el.value || el.textContent || '');

    // Verify the smile emoji appears in the thread text
    const emojiPresent = smileEmojis.some(e => threadText.includes(e));

    return {
      success: emojiPresent,
      action: emojiPresent
        ? `Clicked emoji button, selected smile emoji (${smileEmojiText}), and verified it appears in the thread text`
        : 'Smile emoji was not inserted into the thread text',
      extractedData: {
        insertedEmoji: smileEmojiText,
        threadText,
        emojiPresent
      },
      screenshots,
      error: emojiPresent ? null : 'Smile emoji not found in thread text'
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the compose thread area by its placeholder text (robust against layout changes)
    await page.waitForSelector('textarea[placeholder="Start a thread..."]', { timeout: 5000 });

    // Wait for and click the emoji button using aria-label, then fallback to icon
    // Use the button's title attribute as a selector (different approach from previous attempt)
    const emojiBtn = await page.waitForSelector('button[title="Add emoji"]', { timeout: 4000 });
    await emojiBtn.click();

    // Wait for the emoji picker to appear (look for the emoji grid)
    await page.waitForSelector('.emoji-grid', { timeout: 4000 });

    // Take screenshot after emoji picker is open
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_picker_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_picker_open.png'));

    // Select a smile emoji: search for a .emoji-item containing a smile emoji
    // We'll pick the first emoji-item that contains a classic smile emoji (ðŸ™‚ or ðŸ˜ƒ or ðŸ˜„ or ðŸ˜ or similar)
    // Use evaluate to find the first smiley emoji
    const smileEmojiSelector = await page.evaluateHandle(() => {
      // Try to find a smile emoji in the emoji grid
      const smileRegex = /[\u{1F604}\u{1F603}\u{1F60A}\u{1F60C}\u{263A}\u{1F642}\u{1F60D}\u{1F601}\u{1F606}\u{1F605}\u{1F923}\u{1F60B}\u{1F61B}\u{1F61C}\u{1F61D}\u{1F609}]/u;
      const items = Array.from(document.querySelectorAll('.emoji-item'));
      for (const el of items) {
        if (smileRegex.test(el.textContent)) return el;
      }
      return null;
    });

    if (smileEmojiSelector) {
      // Click the smile emoji
      const elementHandle = smileEmojiSelector.asElement();
      if (elementHandle) {
        await elementHandle.click();
      } else {
        throw new Error('Could not find smile emoji in emoji picker');
      }
    } else {
      throw new Error('Could not locate smile emoji element');
    }

    // Wait for the emoji picker to disappear (either .emoji-grid gone or .emoji-picker-content gone)
    await page.waitForSelector('.emoji-grid', { state: 'detached', timeout: 3000 });

    // Take screenshot after emoji insert
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_inserted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_inserted.png'));

    // Now verify the emoji is present in the textarea
    const threadTextValue = await page.$eval('#thread-text', el => el.value);

    // Extract which smile emoji was added (find it in the value)
    const smilePattern = /[\u{1F604}\u{1F603}\u{1F60A}\u{1F60C}\u{263A}\u{1F642}\u{1F60D}\u{1F601}\u{1F606}\u{1F605}\u{1F923}\u{1F60B}\u{1F61B}\u{1F61C}\u{1F61D}\u{1F609}]/u;
    const foundEmoji = (threadTextValue.match(smilePattern) || [null])[0];

    // Final screenshot for proof
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_final.png'));

    return {
      success: !!foundEmoji,
      action: foundEmoji
        ? `Clicked emoji button, selected smile emoji ("${foundEmoji}"), and verified it appears in thread text`
        : 'Emoji insertion failed, smile emoji not found in thread text',
      extractedData: {
        threadTextValue,
        foundEmoji
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the compose thread textarea to be visible
    await page.waitForSelector('textarea#thread-text', { timeout: 5000 });

    // Wait for the emoji button by icon (fa-smile inside button)
    const emojiBtn = await page.waitForSelector('button#add-emoji,i.fa-smile', { timeout: 5000 });
    // Click the emoji button (using a click on the button id, fallback to icon)
    let emojiButtonClicked = false;
    try {
      // Prefer ID selector
      await page.click('button#add-emoji');
      emojiButtonClicked = true;
    } catch (e) {
      // Fallback: click the button by icon
      const smileIcon = await page.waitForSelector('button:has(i.fa-smile)');
      await smileIcon.click();
      emojiButtonClicked = true;
    }

    // Wait for emoji picker grid to appear (it should contain .emoji-grid and .emoji-item)
    await page.waitForSelector('.emoji-grid .emoji-item', { timeout: 4000 });

    // Find the first "smile" emoji (could be ðŸ˜€, ðŸ˜ƒ, ðŸ˜„, ðŸ˜, ðŸ˜†, or ðŸ™‚, etc.)
    // We'll look for an emoji-item whose textContent matches a typical smile emoji
    // Fallback: just select the first emoji-item
    const smileEmojis = [
      'ðŸ˜€', 'ðŸ˜ƒ', 'ðŸ˜„', 'ðŸ˜', 'ðŸ˜†', 'ðŸ™‚', 'ðŸ˜Š', 'ðŸ˜…', 'ðŸ˜¸', 'ðŸ˜º', 'â˜ºï¸'
    ];
    let smileEmojiSelector = null;
    for (const smile of smileEmojis) {
      const found = await page.$(`.emoji-grid .emoji-item:text-is("${smile}")`);
      if (found) {
        smileEmojiSelector = `.emoji-grid .emoji-item:text-is("${smile}")`;
        break;
      }
    }
    // If not found, select the first emoji-item (as fallback)
    if (!smileEmojiSelector) {
      smileEmojiSelector = '.emoji-grid .emoji-item';
    }

    // Click the smile emoji
    await page.click(smileEmojiSelector);

    // Wait a short moment for emoji to appear in textarea
    await page.waitForTimeout(500);

    // Take screenshot after emoji selection
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_added.png'));

    // Extract the value of the textarea to verify emoji presence
    const threadTextValue = await page.$eval('textarea#thread-text', el => el.value);

    // Find which smile emoji was actually inserted, if any
    const insertedSmile = smileEmojis.find(e => threadTextValue.includes(e));
    const emojiFound = insertedSmile || null;

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_final_check.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_final_check.png'));

    // Check if a smile emoji is present in textarea value
    const emojiPresent = !!emojiFound;

    return {
      success: emojiPresent,
      action: "Clicked emoji button, selected a smile emoji, and verified it appears in the thread text",
      extractedData: {
        threadTextValue,
        emojiFound
      },
      screenshots,
      error: emojiPresent ? null : "Smile emoji was not found in the thread text"
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
