    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_picker_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_picker_open.png'));

    // Find a smile emoji (ðŸ˜Š, ðŸ˜ƒ, ðŸ˜, ðŸ™‚, ðŸ˜„, ðŸ˜†, ðŸ˜…, ðŸ˜, ðŸ˜¸, etc.)
    // We'll try to select a common smile emoji, fallback to first if not found
    const smileEmojis = ['ðŸ˜Š', 'ðŸ˜ƒ', 'ðŸ˜', 'ðŸ™‚', 'ðŸ˜„', 'ðŸ˜†', 'ðŸ˜…', 'ðŸ˜¸', 'ðŸ˜º', 'ðŸ˜ƒ', 'ðŸ˜€', 'ðŸ˜‹', 'ðŸ˜Ž'];
    let smileEmojiSelector = null;
    for (const emoji of smileEmojis) {
      const emojiHandle = await page.$(`.emoji-item:text("${emoji}")`);
      if (emojiHandle) {
        smileEmojiSelector = `.emoji-item:text("${emoji}")`;
        break;
