    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_inserted.png'));

    // Extract the value of the thread text area
    await page.waitForSelector('#thread-text', { state: 'visible', timeout: 3000 });
    const threadText = await page.$eval('#thread-text', el => el.value);

    // Find which smile emoji was inserted
    let foundEmoji = null;
    for (const emoji of smileEmojis) {
      if (threadText.includes(emoji)) {
        foundEmoji = emoji;
        break;
      }
    }
    // If not found, try to extract any emoji character from the text
    if (!foundEmoji) {
      // Unicode emoji regex (basic)
      const emojiMatch = threadText.match(/[\u{1F600}-\u{1F64F}]/u);
      foundEmoji = emojiMatch ? emojiMatch[0] : null;
    }

    // Take screenshot of the text area with emoji
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_textarea_with_emoji.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_textarea_with_emoji.png'));

    // Prepare extracted data
    const extractedData = {
      threadText,
      emojiFound: foundEmoji,
      emojiPresent: !!foundEmoji
    };

    return {
      success: !!foundEmoji,
      action: foundEmoji
        ? `Clicked emoji button, selected smile emoji "${foundEmoji}", and verified it appears in the thread text`
        : "Clicked emoji button, selected emoji, but could not verify a smile emoji in the thread text",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the emoji button in the compose thread actions area (icon with smile)
    // Try to use aria-label, role, or fallback to XPath on icon class
    const emojiBtnSelector = '#add-emoji';
    await page.waitForSelector(emojiBtnSelector, { timeout: 5000 });
    // Extra check: button should be visible and enabled
    const emojiBtn = await page.$(emojiBtnSelector);
    if (!emojiBtn) throw new Error('Emoji button not found');
    await emojiBtn.click();

    // Wait for emoji picker to appear - look for the emoji grid by class
    // Try class .emoji-grid or .emoji-item, fallback to visible emoji
    await page.waitForSelector('.emoji-grid', { timeout: 3000 });

    // Take a screenshot of the opened emoji picker
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_picker_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_picker_open.png'));

    // Select a smile emoji - look for a visible emoji with a smile face
    // Try to pick a standard smile emoji from the grid
    // Use XPath to select an emoji-item that has a smiley (ðŸ˜Š, ðŸ˜ƒ, ðŸ™‚, etc.)
    // Fallback: Just pick the first .emoji-item in the grid

    const smileEmojiHandles = await page.$$('.emoji-item');
    let smileEmojiHandle = null;
    let smileEmojiText = null;
    for (const emojiEl of smileEmojiHandles) {
      const emojiText = await emojiEl.textContent();
      if (
        emojiText &&
        (
          emojiText.includes('ðŸ˜Š') ||
          emojiText.includes('ðŸ˜ƒ') ||
          emojiText.includes('ðŸ™‚') ||
          emojiText.includes('ðŸ˜„') ||
          emojiText.includes('ðŸ˜') ||
          emojiText.includes('ðŸ˜†') ||
          emojiText.includes('ðŸ˜…') ||
          emojiText.includes('ðŸ˜¸') // allow a cat smile too
        )
      ) {
        smileEmojiHandle = emojiEl;
        smileEmojiText = emojiText.trim();
        break;
      }
    }
    // If no typical smile emoji found, pick first emoji as fallback
    if (!smileEmojiHandle && smileEmojiHandles.length > 0) {
      smileEmojiHandle = smileEmojiHandles[0];
      smileEmojiText = (await smileEmojiHandles[0].textContent()).trim();
    }
    if (!smileEmojiHandle) throw new Error('No emoji item found in emoji picker');

    // Click the smile emoji
    await smileEmojiHandle.click();

    // Wait for emoji picker to close (emoji grid should disappear)
    await page.waitForSelector('.emoji-grid', { state: 'hidden', timeout: 3000 });

    // Take a screenshot after emoji was inserted
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_inserted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_inserted.png'));

    // Verify that the emoji appears in the thread text area
    // The text area has id="thread-text"
    await page.waitForSelector('#thread-text');
    const threadTextValue = await page.$eval('#thread-text', el => el.value);

    // Check if the text area value includes the emoji we inserted
    let emojiFound = false;
    if (threadTextValue && smileEmojiText && threadTextValue.includes(smileEmojiText)) {
      emojiFound = true;
    }

    // Take a final screenshot for verification
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_final_verification.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_final_verification.png'));

    return {
      success: emojiFound,
      action: "Clicked emoji button, selected a smile emoji, and verified it appears in the thread text area",
      extractedData: {
        insertedEmoji: smileEmojiText,
        threadText: threadTextValue,
        emojiFound
      },
      screenshots,
      error: emojiFound ? null : 'Emoji was not inserted into the thread text area'
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the compose section to be visible (by textarea placeholder)
    await page.waitForSelector('textarea[placeholder="Start a thread..."]', { timeout: 5000 });

    // Wait for the emoji button (try by aria-label, fallback to id and icon)
    let emojiBtn = await page.$('button[title="Add emoji"]');
    if (!emojiBtn) {
      // Fallback: try by id
      emojiBtn = await page.$('#add-emoji');
    }
    if (!emojiBtn) {
      // Fallback: try by icon (fa-smile)
      emojiBtn = await page.$('button:has(i.fa-smile)');
    }
    if (!emojiBtn) {
      throw new Error('Emoji button not found');
    }

    // Click the emoji button
    await emojiBtn.click();

    // Wait for the emoji picker grid (using .emoji-grid or .emoji-item)
    await page.waitForSelector('.emoji-grid, .emoji-item', { timeout: 3000 });

    // Select a "smile" emoji - look for standard smileys
    // Try finding a common smile emoji, e.g., ðŸ˜€, ðŸ˜ƒ, ðŸ˜„, ðŸ˜, ðŸ˜Š, ðŸ˜ƒ, ðŸ˜†, ðŸ˜…, ðŸ˜‚, ðŸ™‚, or similar
    // We'll try to pick the first available emoji in the grid, then check for a smile one

    // Try to pick one that matches a smile (regex)
    const smileEmojiSelector = '.emoji-item';
    await page.waitForSelector(smileEmojiSelector, { timeout: 3000 });
    const emojiHandles = await page.$$(smileEmojiSelector);

    let smileEmojiHandle = null;
    let smileEmojiText = null;
    for (const handle of emojiHandles) {
      const emojiChar = await handle.innerText();
      // Use a regex to match smileys (Unicode ranges for common smileys)
      if (
        /[\u263A\u263B\u1F600-\u1F60D\u1F642-\u1F644\u1F60A\u1F603\u1F604\u1F60D\u1F618\u1F617\u1F609\u1F619\u1F61A\u1F60B\u1F61B\u1F61C\u1F61D\u1F972\u1F60E\u1F929\u1F973\u1F60F\u1F636\u1F607]/.test(
          emojiChar
        )
      ) {
        smileEmojiHandle = handle;
        smileEmojiText = emojiChar;
        break;
      }
    }
    // Fallback: just pick the first emoji if smiley not found
    if (!smileEmojiHandle && emojiHandles.length > 0) {
      smileEmojiHandle = emojiHandles[0];
      smileEmojiText = await smileEmojiHandle.innerText();
    }
    if (!smileEmojiHandle) {
      throw new Error('No emoji item found in picker');
    }

    // Click the smile emoji
    await smileEmojiHandle.click();

    // Take screenshot after emoji selection
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_selected.png'));

    // Wait for emoji to be inserted into the thread textarea
    const threadTextarea = await page.$('textarea#thread-text');
    await page.waitForTimeout(500); // give time for update

    // Extract the thread text value
    const threadTextValue = await threadTextarea.evaluate(node => node.value);

    // Take screenshot of the thread text with emoji
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_in_text.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_in_text.png'));

    // Check if the smile emoji is present in the textarea value
    const emojiPresent = threadTextValue.includes(smileEmojiText);

    return {
      success: emojiPresent,
      action: "Clicked emoji button, selected a smile emoji, and verified it appears in the thread text area",
      extractedData: {
        insertedEmoji: smileEmojiText,
        threadText: threadTextValue,
        emojiPresent
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
