    await page.waitForSelector('#add-emoji', { state: 'visible', timeout: 5000 });

    // Click the emoji button
    await page.click('#add-emoji');
    // Wait for emoji picker to appear (look for .emoji-item)
    await page.waitForSelector('.emoji-item', { state: 'visible', timeout: 5000 });

    // Take screenshot of emoji picker open
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_picker_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_picker_open.png'));

    // Find a smile emoji in the picker
    // We'll look for an emoji-item whose textContent matches a common smile emoji
    // Try several common smile emojis in order
    const smileEmojis = ['ðŸ˜ƒ', 'ðŸ˜„', 'ðŸ˜Š', 'ðŸ™‚', 'ðŸ˜', 'ðŸ˜†', 'ðŸ˜…', 'ðŸ˜€', 'ðŸ˜¸', 'ðŸ˜º', 'ðŸ˜Ž', 'ðŸ˜‹', 'ðŸ˜‡', 'ðŸ¥°', 'ðŸ˜', 'ðŸ¤—', 'â˜ºï¸'];
    let foundEmoji = null;
    for (const emoji of smileEmojis) {
      const emojiLocator = page.locator(`.emoji-item:text-is("${emoji}")`);
      if (await emojiLocator.count() > 0) {
        foundEmoji = emoji;
        await emojiLocator.first().click();
        break;
      }
    }

    // If not found, fallback to clicking the first emoji
    if (!foundEmoji) {
      const firstEmoji = page.locator('.emoji-item').first();
      foundEmoji = await firstEmoji.textContent();
      await firstEmoji.click();
    }

    // Wait for emoji picker to disappear (optional, but safer)
    await page.waitForTimeout(300); // short wait for UI update

    // Take screenshot after emoji is inserted
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_inserted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_inserted.png'));

    // Extract the thread text area value
    await page.waitForSelector('#thread-text', { state: 'visible', timeout: 2000 });
    const threadText = await page.$eval('#thread-text', el => el.value);

    // Check if the inserted emoji is present in the thread text
    let emojiFoundInText = false;
    let matchedEmoji = null;
    for (const emoji of smileEmojis.concat([foundEmoji])) {
      if (threadText && threadText.includes(emoji)) {
        emojiFoundInText = true;
        matchedEmoji = emoji;
        break;
      }
    }

    return {
      success: emojiFoundInText,
      action: emojiFoundInText
        ? `Clicked emoji button, selected a smile emoji ("${matchedEmoji}"), and verified it appears in the thread text`
        : "Clicked emoji button and selected an emoji, but could not verify it in the thread text",
      extractedData: {
        insertedEmoji: matchedEmoji,
        threadText
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');
    await page.waitForSelector('textarea#thread-text', { timeout: 4000 });

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // 1. Click the emoji button (use aria-label or icon as alternative selector)
    // Try to use the button with title="Add emoji", fallback to #add-emoji
    let emojiBtn = await page.$('button[title="Add emoji"]');
    if (!emojiBtn) emojiBtn = await page.$('#add-emoji');
    if (!emojiBtn) throw new Error("Emoji button not found");
    await emojiBtn.click();

    // 2. Wait for emoji picker to appear (wait for .emoji-item or grid)
    await page.waitForSelector('.emoji-grid', { timeout: 4000 });
    await page.waitForSelector('.emoji-item', { timeout: 4000 });

    // Take screenshot after emoji picker is open
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_picker_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_picker_open.png'));

    // 3. Select a smile emoji (try to pick a classic smile emoji from the picker)
    // Find emoji by its unicode (common smileys: ðŸ˜€, ðŸ˜ƒ, ðŸ˜„, ðŸ˜Š, ðŸ˜, ðŸ˜†, ðŸ˜…, ðŸ˜‚, ðŸ™‚, ðŸ˜ƒ)
    // We'll look for an emoji-item containing one of the common smileys
    const smileyEmojis = ['ðŸ˜€', 'ðŸ˜ƒ', 'ðŸ˜„', 'ðŸ˜', 'ðŸ˜Š', 'ðŸ™‚', 'ðŸ˜†', 'ðŸ˜…', 'ðŸ˜‚'];
    let foundEmoji = null;
    for (const emoji of smileyEmojis) {
      const emojiElement = await page.$(`.emoji-item:text-is("${emoji}")`);
      if (emojiElement) {
        foundEmoji = emojiElement;
        break;
      }
    }
    // Fallback: pick the first emoji-item if smiley not found
    if (!foundEmoji) {
      const allEmojiItems = await page.$$('.emoji-item');
      if (allEmojiItems.length === 0) throw new Error("No emoji items found in picker");
      foundEmoji = allEmojiItems[0];
    }
    // Get the emoji character for verification
    const emojiChar = await foundEmoji.evaluate(el => el.textContent.trim());
    await foundEmoji.click();

    // Take screenshot after emoji is selected (emoji picker may close)
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_selected.png'));

    // 4. Verify the emoji appears in the thread text area
    // Wait a moment for emoji to be inserted
    await page.waitForTimeout(500);
    // Get the value of the textarea
    const threadText = await page.$eval('#thread-text', el => el.value);

    // Take screenshot of the filled textarea
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_textarea_with_emoji.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_textarea_with_emoji.png'));

    // Check if the emoji is present in the textarea value
    const emojiPresent = threadText.includes(emojiChar);

    return {
      success: emojiPresent,
      action: `Clicked emoji button, selected emoji "${emojiChar}", and verified it appears in thread text`,
      extractedData: {
        selectedEmoji: emojiChar,
        threadText,
        emojiPresent
      },
      screenshots,
      error: emojiPresent ? null : "Emoji was not inserted into the thread text"
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Step 1: Initial screenshot of the page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Step 2: Wait for the emoji button (try by aria-label, then fallback to ID and class)
    let emojiBtnSelector = 'button[title="Add emoji"]';
    await page.waitForSelector(emojiBtnSelector, { timeout: 3000 });
    const emojiBtn = await page.$(emojiBtnSelector);

    if (!emojiBtn) {
      // Fallback: by icon content
      emojiBtnSelector = '#add-emoji';
      await page.waitForSelector(emojiBtnSelector, { timeout: 3000 });
    }

    // Step 3: Click the emoji button to open the emoji picker
    await page.click(emojiBtnSelector);
    await page.waitForTimeout(500); // Wait for the emoji picker animation

    // Step 4: Wait for emoji picker grid to appear
    // Try CSS grid class, then fallback to emoji-item
    let emojiGridSelector = '.emoji-grid';
    let emojiItemSelector = '.emoji-item';
    await page.waitForSelector(emojiGridSelector, { timeout: 3000 }).catch(async () => {
      // Fallback: emoji-item directly
      await page.waitForSelector(emojiItemSelector, { timeout: 3000 });
    });

    // Step 5: Pick a smile emoji (look for an emoji-item containing ðŸ™‚ or ðŸ˜€ or ðŸ˜ƒ or ðŸ˜)
    // Use page.$$ and textContent search, fallback to first .emoji-item

    let smileEmoji = null;
    const smileEmojis = ['ðŸ˜€', 'ðŸ˜ƒ', 'ðŸ˜„', 'ðŸ˜', 'ðŸ˜Š', 'ðŸ™‚', 'ðŸ˜…', 'ðŸ˜†', 'ðŸ˜¸', 'ðŸ˜º'];

    const emojiElements = await page.$$(emojiItemSelector);
    for (const emojiEl of emojiElements) {
      const emojiText = await page.evaluate(el => el.textContent, emojiEl);
      if (smileEmojis.some(smile => emojiText && emojiText.includes(smile))) {
        smileEmoji = emojiEl;
        break;
      }
    }
    // If no smile emoji found, fallback to the first emoji
    if (!smileEmoji && emojiElements.length > 0) {
      smileEmoji = emojiElements[0];
    }

    // Step 6: Click the smile emoji
    if (!smileEmoji) {
      throw new Error('No emoji found in picker');
    }
    await smileEmoji.click();

    // Wait for the emoji picker to disappear and for the emoji to appear in textarea
    await page.waitForTimeout(300);

    // Step 7: Take screenshot after emoji is inserted
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_added.png'));

    // Step 8: Extract the textarea value to verify the emoji is present
    const textareaSelector = '#thread-text';
    await page.waitForSelector(textareaSelector, { timeout: 2000 });
    const threadText = await page.$eval(textareaSelector, el => el.value);

    // Find which smile emoji actually appeared (if any)
    let foundEmoji = null;
    if (threadText) {
      foundEmoji = smileEmojis.find(smile => threadText.includes(smile));
    }

    // Step 9: Final screenshot (redundant but for completeness)
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_final.png'));

    return {
      success: !!foundEmoji,
      action: "Clicked emoji button, selected a smile emoji, and verified it appears in the thread text area.",
      extractedData: {
        threadTextValue: threadText,
        detectedEmoji: foundEmoji
      },
      screenshots,
      error: foundEmoji ? null : "No smile emoji detected in thread text area"
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
