import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the emoji button to be visible
    await page.waitForSelector('#add-emoji', { state: 'visible', timeout: 5000 });

    // Click the emoji button to open the emoji picker
    await page.click('#add-emoji');

    // Wait for the emoji picker to appear (look for .emoji-item)
    await page.waitForSelector('.emoji-item', { state: 'visible', timeout: 5000 });

    // Take screenshot of emoji picker open
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_picker_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_picker_open.png'));

    // Find a smile emoji in the picker (commonly 'ðŸ˜ƒ', 'ðŸ˜Š', 'ðŸ™‚', 'ðŸ˜', etc.)
    // We'll try to select the first emoji that matches a smile
    const smileEmojis = ['ðŸ˜ƒ', 'ðŸ˜Š', 'ðŸ™‚', 'ðŸ˜', 'ðŸ˜„', 'ðŸ˜†', 'ðŸ˜…', 'ðŸ˜€', 'ðŸ˜¸', 'ðŸ˜º'];
    let smileEmojiSelector = null;
    for (const emoji of smileEmojis) {
      // Try to find the emoji-item with this emoji as text
      const emojiElement = await page.$(`.emoji-item:text("${emoji}")`);
      if (emojiElement) {
        smileEmojiSelector = `.emoji-item:text("${emoji}")`;
        break;
      }
    }

    // If Playwright's :text() doesn't work, fallback to evaluating all emoji-items and pick the first smile
    let selectedEmoji = null;
    if (!smileEmojiSelector) {
      const emojiHandles = await page.$$('.emoji-item');
      for (const handle of emojiHandles) {
        const text = await handle.textContent();
        if (smileEmojis.includes(text && text.trim())) {
          selectedEmoji = text.trim();
          await handle.click();
          break;
        }
      }
    } else {
      await page.click(smileEmojiSelector);
      selectedEmoji = smileEmojis.find(e => smileEmojiSelector.includes(e));
    }

    // Take screenshot after emoji is inserted
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_inserted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_inserted.png'));

    // Wait for the emoji to appear in the thread text area
    await page.waitForSelector('#thread-text', { state: 'visible', timeout: 3000 });

    // Extract the value of the thread text area
    const threadTextValue = await page.$eval('#thread-text', el => el.value);

    // Check if any smile emoji is present in the textarea value
    const foundEmoji = smileEmojis.find(e => threadTextValue.includes(e));

    // Take screenshot of the textarea with emoji
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_textarea_with_emoji.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_textarea_with_emoji.png'));

    return {
      success: !!foundEmoji,
      action: foundEmoji
        ? `Clicked emoji button, selected smile emoji (${foundEmoji}), and verified it appears in the thread text`
        : "Clicked emoji button and attempted to select a smile emoji, but could not verify its presence in the thread text",
      extractedData: {
        emojiInserted: foundEmoji || null,
        threadText: threadTextValue
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file and wait for page to be idle
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const initialScreenshot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialScreenshot });
    screenshots.push(initialScreenshot);

    // Make sure the "Start a thread..." textarea is visible
    await page.waitForSelector('textarea[placeholder="Start a thread..."]', { state: 'visible' });

    // Wait for the emoji button to be visible by aria-label or icon
    // Use alternative selector: find tool button by title attribute and icon
    const emojiBtn = await page.waitForSelector('button.tool-btn[title*="emoji"], button#add-emoji, button.tool-btn:has(i.fa-smile)', { state: 'visible', timeout: 3000 });

    // Click the emoji button to open the emoji picker
    await emojiBtn.click();

    // Wait for the emoji picker/grid to appear
    // Use alternative selector strategy: CSS grid class
    await page.waitForSelector('.emoji-grid', { state: 'visible', timeout: 3000 });

    // Find the first emoji that looks like a "smile" emoji.
    // We'll pick the first emoji-item containing a smiley (â˜ºï¸, ðŸ™‚, ðŸ˜„, ðŸ˜ƒ, ðŸ˜€, ðŸ˜, ðŸ˜Š, ðŸ˜…, ðŸ˜‚, ðŸ˜†, ðŸ˜º, ðŸ˜¸, etc.)
    // Try to select a recognizable smile-related emoji
    const smileyEmojiSelectors = [
      '.emoji-grid .emoji-item:has-text("ðŸ˜ƒ")',
      '.emoji-grid .emoji-item:has-text("ðŸ˜„")',
      '.emoji-grid .emoji-item:has-text("ðŸ˜Š")',
      '.emoji-grid .emoji-item:has-text("ðŸ˜„")',
      '.emoji-grid .emoji-item:has-text("ðŸ™‚")',
      '.emoji-grid .emoji-item:has-text("ðŸ˜")',
      '.emoji-grid .emoji-item:has-text("ðŸ˜€")',
      '.emoji-grid .emoji-item:has-text("ðŸ˜…")',
      '.emoji-grid .emoji-item:has-text("ðŸ˜†")',
      '.emoji-grid .emoji-item:has-text("ðŸ˜‚")'
    ];
    let emojiFound = false;
    let selectedEmoji = '';
    for (const sel of smileyEmojiSelectors) {
      const emoji = await page.$(sel);
      if (emoji) {
        selectedEmoji = await emoji.textContent();
        await emoji.click();
        emojiFound = true;
        break;
      }
    }
    if (!emojiFound) {
      // fallback: click the first emoji-item in the grid
      const firstEmoji = await page.$('.emoji-grid .emoji-item');
      if (!firstEmoji) {
        throw new Error('Could not find any emoji to click');
      }
      selectedEmoji = await firstEmoji.textContent();
      await firstEmoji.click();
    }

    // Wait a short moment for the emoji to be inserted
    await page.waitForTimeout(500);

    // Take screenshot after emoji insert
    const afterEmojiScreenshot = path.join(screenshotsDir, 'step_2_emoji_inserted.png');
    await page.screenshot({ path: afterEmojiScreenshot });
    screenshots.push(afterEmojiScreenshot);

    // Check the textarea value to verify the emoji was inserted
    const threadTextArea = await page.$('textarea[placeholder="Start a thread..."]');
    const threadValue = await threadTextArea.evaluate(el => el.value);

    // Check if the selected emoji is present in the thread text
    const emojiPresent = selectedEmoji && threadValue.includes(selectedEmoji);

    // Take final screenshot
    const finalScreenshot = path.join(screenshotsDir, 'step_3_verification.png');
    await page.screenshot({ path: finalScreenshot });
    screenshots.push(finalScreenshot);

    return {
      success: emojiPresent,
      action: `Clicked emoji button, selected emoji "${selectedEmoji}", and verified it appears in the thread text`,
      extractedData: {
        selectedEmoji,
        threadText: threadValue,
        emojiPresent
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the emoji button using aria-label and icon as backup selector
    const emojiButtonSelector = 'button#add-emoji, button[title="Add emoji"], button.tool-btn:has(i.fa-smile)';
    await page.waitForSelector(emojiButtonSelector, { timeout: 5000 });

    // Scroll emoji button into view and click
    await page.$eval(emojiButtonSelector, el => el.scrollIntoView({behavior: 'auto', block: 'center'}));
    await page.click(emojiButtonSelector);

    // Wait for the emoji picker to appear (look for emoji-item class)
    // Wait for at least one emoji-item to appear
    await page.waitForSelector('.emoji-item', { timeout: 5000 });

    // Find all emoji items and try to pick a "smile" emoji
    // We'll look for any of the common smile unicode emojis
    // If not found, just click the first emoji as fallback

    // List of common smile emojis
    const smileEmojis = ['ðŸ˜ƒ','ðŸ˜„','ðŸ˜†','ðŸ™‚','ðŸ˜Š','ðŸ˜','ðŸ˜…','ðŸ˜¸','ðŸ˜º','ðŸ˜ƒ','ðŸ˜Ž','ðŸ˜‡','ðŸ˜‰','ðŸ˜€','ðŸ˜‹','ðŸ˜','ðŸ¥°','ðŸ˜','ðŸ˜š','ðŸ˜™','ðŸ˜—','â˜ºï¸','ðŸ˜Œ','ðŸ˜ƒ','ðŸ˜º','ðŸ˜»','ðŸ˜¸'];

    // Evaluate all emoji-items' textContent and pick the first smile
    const emojiHandle = await page.evaluateHandle((smiles) => {
      const emojiNodes = Array.from(document.querySelectorAll('.emoji-item'));
      for (const node of emojiNodes) {
        if (smiles.includes(node.textContent.trim())) {
          return node;
        }
      }
      // fallback: first emoji
      return emojiNodes[0] || null;
    }, smileEmojis);

    // If found, click the emoji
    if (emojiHandle) {
      const element = await emojiHandle.asElement();
      if (element) {
        await element.scrollIntoViewIfNeeded();
        await element.click();
      }
    }

    // Screenshot after emoji selection
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_selected.png'));

    // Wait for emoji to appear in the thread textarea
    // The thread textarea has id #thread-text
    await page.waitForSelector('#thread-text', { timeout: 2000 });

    // Wait until the textarea value contains a smile emoji, or any emoji if no smile found
    // We'll check repeatedly for up to 2 seconds
    const emojiInserted = await page.waitForFunction(
      (smiles) => {
        const ta = document.querySelector('#thread-text');
        if (!ta) return false;
        const val = ta.value || ta.textContent || '';
        // check for any smile emoji
        for (const emoji of smiles) {
          if (val.includes(emoji)) return emoji;
        }
        // fallback: check for any emoji
        const emojiRegex = /\p{Emoji}/u;
        return emojiRegex.test(val) ? val.match(/\p{Emoji}/u)[0] : false;
      },
      smileEmojis,
      { timeout: 2000 }
    );

    // Get the emoji value in the textarea
    const textareaValue = await page.$eval('#thread-text', el => el.value);

    // Screenshot after emoji is inserted
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_inserted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_inserted.png'));

    // Extract the emoji that appears in the textarea (if any)
    const matchedEmoji = (textareaValue.match(/\p{Emoji}/u) || [null])[0];

    return {
      success: !!matchedEmoji,
      action: matchedEmoji
        ? `Clicked emoji button, selected a smile emoji, and verified it appears in the thread text (${matchedEmoji})`
        : 'Clicked emoji button, selected emoji, but no emoji found in thread text',
      extractedData: {
        threadText: textareaValue,
        emojiFound: matchedEmoji
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
