import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Step 1: Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Step 2: Wait for the emoji button to be present and visible
    // Try multiple strategies: by id, by aria-label, by icon, by tooltip
    // The emoji button has id="add-emoji" and class="tool-btn"
    const emojiButtonSelector = '#add-emoji';
    await page.waitForSelector(emojiButtonSelector, { state: 'visible', timeout: 5000 });

    // Step 3: Click the emoji button to open the emoji picker
    await page.click(emojiButtonSelector);

    // Wait for the emoji picker to appear
    // The emoji picker uses a grid with class "emoji-grid"
    // Each emoji is ".emoji-item"
    await page.waitForSelector('.emoji-grid', { state: 'visible', timeout: 3000 });

    // Step 4: Select a "smile" emoji
    // We'll try to pick a smiley emoji by inspecting textContent of emoji-item elements
    // Typical smiley emojis: ðŸ˜ƒ, ðŸ˜„, ðŸ™‚, ðŸ˜, ðŸ˜Š, ðŸ˜†, ðŸ˜…, ðŸ˜, ðŸ˜Ž, ðŸ˜€, ðŸ˜ƒ, ðŸ˜„, ðŸ˜, etc.
    // We'll look for any emoji containing a smile (Unicode range)
    const smileyEmojis = ['ðŸ˜€','ðŸ˜ƒ','ðŸ˜„','ðŸ˜','ðŸ˜†','ðŸ˜…','ðŸ˜‚','ðŸ™‚','ðŸ˜Š','ðŸ˜Ž','ðŸ¥°','ðŸ˜','ðŸ˜˜','ðŸ˜‡','ðŸ˜º','ðŸ˜¸'];
    // Find all emoji items, pick the first that matches
    const emojiHandles = await page.$$('.emoji-item');
    let selectedEmoji = null;
    for (const handle of emojiHandles) {
      const emojiText = await handle.textContent();
      if (smileyEmojis.includes(emojiText.trim())) {
        await handle.click();
        selectedEmoji = emojiText.trim();
        break;
      }
    }
    if (!selectedEmoji) {
      // If not found, just click the first emoji as fallback
      if (emojiHandles.length > 0) {
        selectedEmoji = await emojiHandles[0].textContent();
        await emojiHandles[0].click();
      } else {
        throw new Error('No emoji found in emoji picker');
      }
    }

    // Wait for the emoji picker to disappear (optional, robustness)
    await page.waitForSelector('.emoji-grid', { state: 'detached', timeout: 2000 }).catch(() => {});

    // Step 5: Verify the emoji appears in the thread text area
    // The textarea has id="thread-text"
    await page.waitForSelector('#thread-text', { state: 'visible', timeout: 2000 });
    const threadValue = await page.$eval('#thread-text', el => el.value || el.textContent || el.innerText);

    // Take screenshot after emoji insertion
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_added.png'));

    // Verification: the selectedEmoji should appear in the textarea value
    const emojiPresent = threadValue && threadValue.includes(selectedEmoji);

    // Final result
    return {
      success: emojiPresent,
      action: `Clicked emoji button, selected emoji "${selectedEmoji}", verified its presence in the thread text`,
      extractedData: {
        selectedEmoji,
        threadText: threadValue,
        emojiPresent
      },
      screenshots,
      error: emojiPresent ? null : `Emoji "${selectedEmoji}" not found in thread text area`
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Step 1: Navigate to the HTML file and wait for network to be idle
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Step 2: Wait for the emoji button to be visible using aria-label as alternative strategy
    const emojiBtnSelector = 'button[title="Add emoji"], #add-emoji, .tool-btn:has(i.fa-smile)';
    await page.waitForSelector(emojiBtnSelector, { timeout: 7000 });

    // Take a screenshot before clicking emoji button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Step 3: Click the emoji button using alternative selector
    const emojiBtn = await page.$(emojiBtnSelector);
    await emojiBtn.click();

    // Step 4: Wait for emoji picker popup to appear (using .emoji-item as indicator)
    await page.waitForSelector('.emoji-item', { timeout: 7000 });

    // Take a screenshot of the emoji picker
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_picker_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_picker_opened.png'));

    // Step 5: Select a smile emoji (try to pick a smiley face using its unicode if possible)
    // We'll select the first emoji that contains a smile. Try to find emoji containing 'ðŸ˜ƒ', 'ðŸ˜Š', 'ðŸ™‚', etc.
    const smileyEmojis = ['ðŸ˜ƒ', 'ðŸ˜Š', 'ðŸ™‚', 'ðŸ˜', 'ðŸ˜„', 'ðŸ˜†', 'ðŸ˜…', 'ðŸ˜‚', 'ðŸ˜º'];
    let emojiFound = false;
    let selectedEmoji = null;

    for (const smile of smileyEmojis) {
      const emojiElement = await page.$(`.emoji-item:text("${smile}")`);
      if (emojiElement) {
        await emojiElement.click();
        emojiFound = true;
        selectedEmoji = smile;
        break;
      }
    }

    // If not found with :text(), fallback to evaluating all emoji-item elements and click the first smiley
    if (!emojiFound) {
      const emojiHandles = await page.$$('.emoji-item');
      for (const handle of emojiHandles) {
        const emojiText = await handle.evaluate(node => node.textContent.trim());
        if (smileyEmojis.some(e => emojiText.includes(e))) {
          await handle.click();
          emojiFound = true;
          selectedEmoji = emojiText;
          break;
        }
      }
    }

    // If still not found, just click the first emoji-item as fallback
    if (!emojiFound) {
      const firstEmoji = await page.$('.emoji-item');
      if (firstEmoji) {
        selectedEmoji = await firstEmoji.evaluate(node => node.textContent.trim());
        await firstEmoji.click();
        emojiFound = true;
      }
    }

    // Wait briefly for emoji to be inserted into the textarea
    await page.waitForTimeout(500);

    // Take a screenshot after inserting emoji
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_inserted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_inserted.png'));

    // Step 6: Extract the thread text value to verify the emoji is present
    const textareaSelector = '#thread-text, textarea[placeholder="Start a thread..."]';
    await page.waitForSelector(textareaSelector);

    const threadText = await page.$eval(textareaSelector, el => el.value);

    let foundInText = false;
    let foundEmoji = null;
    for (const smile of smileyEmojis) {
      if (threadText.includes(smile)) {
        foundInText = true;
        foundEmoji = smile;
        break;
      }
    }
    if (!foundInText && selectedEmoji && threadText.includes(selectedEmoji)) {
      foundInText = true;
      foundEmoji = selectedEmoji;
    }

    return {
      success: foundInText,
      action: foundInText
        ? `Clicked emoji button, selected smile emoji (${foundEmoji || 'unknown'}), and verified it appears in the thread text`
        : 'Clicked emoji button and selected emoji, but could not verify a smile emoji in the thread text',
      extractedData: {
        threadText,
        foundEmoji: foundEmoji || selectedEmoji,
        emojiInserted: foundInText
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
