import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Step 1: Initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Step 2: Wait for and click the emoji button (using aria-label as an alternative and icon class fallback)
    // Prefer button with aria-label or title, else fallback to icon
    const emojiButtonSelector = 'button[title="Add emoji"], button#add-emoji, .tool-btn:has(.fa-smile)';
    await page.waitForSelector(emojiButtonSelector, { timeout: 5000 });
    const emojiBtn = await page.$(emojiButtonSelector);
    if (!emojiBtn) throw new Error('Emoji button not found');
    await emojiBtn.click();

    // Wait for emoji picker to appear (by .emoji-item or emoji grid)
    await page.waitForSelector('.emoji-item', { timeout: 5000 });

    // Step 3: Select a "smile" emoji
    // Try to select the first emoji that is a classic smile, else first emoji
    // Common Unicode: ðŸ˜€ ðŸ˜ƒ ðŸ˜„ ðŸ˜ ðŸ˜† ðŸ˜… ðŸ˜‚ ðŸ˜Š ðŸ™‚ ðŸ™ƒ ðŸ˜‡ ðŸ˜‰ ðŸ˜
    // We'll search for emoji-item with a smile emoji (using XPath for robust selection)
    const smileEmojiXpaths = [
      "//div[contains(@class,'emoji-item') and (text()='ðŸ˜€' or text()='ðŸ˜ƒ' or text()='ðŸ˜„' or text()='ðŸ˜Š' or text()='ðŸ™‚' or text()='ðŸ˜' or text()='ðŸ˜†' or text()='ðŸ˜…' or text()='ðŸ˜‚')]",
      "//div[contains(@class,'emoji-item')]"
    ];

    let emojiElement = null;
    for (const xpath of smileEmojiXpaths) {
      const handles = await page.$x(xpath);
      if (handles.length > 0) {
        emojiElement = handles[0];
        break;
      }
    }
    if (!emojiElement) throw new Error('No emoji found in picker');
    // Save screenshot before clicking emoji
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_picker.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_picker.png'));

    // Get the emoji character for later verification
    const selectedEmoji = await emojiElement.evaluate(el => el.textContent.trim());
    await emojiElement.click();

    // Step 4: Verify emoji appears in the thread text area
    // Wait for textarea to contain the emoji (with retries)
    const threadTextSelector = '#thread-text, textarea[placeholder="Start a thread..."]';
    await page.waitForSelector(threadTextSelector, { timeout: 5000 });
    let emojiInTextarea = false;
    let textareaValue = '';
    for (let i = 0; i < 10; i++) {
      textareaValue = await page.$eval(threadTextSelector, el => el.value || el.textContent || '');
      if (textareaValue.includes(selectedEmoji)) {
        emojiInTextarea = true;
        break;
      }
      await page.waitForTimeout(200);
    }

    // Screenshot after insertion
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_inserted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_inserted.png'));

    if (!emojiInTextarea) {
      throw new Error('Emoji was not inserted into the thread text area');
    }

    return {
      success: true,
      action: `Clicked emoji button, selected smile emoji "${selectedEmoji}", and verified its appearance in thread text.`,
      extractedData: {
        insertedEmoji: selectedEmoji,
        threadText: textareaValue
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Step 1: Navigate to the HTML file and wait for network to be idle
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const step1 = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: step1 });
    screenshots.push(step1);

    // Step 2: Wait for the emoji button using alternative selector strategies
    // Try aria-label, title, CSS class, and fallback to id
    let emojiBtn;
    try {
      // Try by title attribute first
      emojiBtn = await page.waitForSelector('button[title="Add emoji"]', { timeout: 2000 });
    } catch {
      // Fallback to icon within button (font-awesome smile)
      emojiBtn = await page.waitForSelector('button:has(i.fa-smile)', { timeout: 2000 });
    }
    if (!emojiBtn) {
      // Fallback to ID if above fails
      emojiBtn = await page.waitForSelector('#add-emoji', { timeout: 3000 });
    }

    // Highlight and click the emoji button
    await emojiBtn.hover();
    await emojiBtn.click();

    // Wait for the emoji picker to appear (look for .emoji-item)
    await page.waitForSelector('.emoji-item', { timeout: 3000 });

    // Take screenshot of emoji picker opened
    const step2 = path.join(screenshotsDir, 'step_2_emoji_picker_opened.png');
    await page.screenshot({ path: step2 });
    screenshots.push(step2);

    // Step 3: Select a smile emoji
    // Try to find any emoji-item with a common smile emoji (ðŸ˜Š, ðŸ˜ƒ, ðŸ˜€, ðŸ˜, ðŸ˜„, ðŸ™‚, etc.)
    const knownSmileEmojis = ['ðŸ˜Š', 'ðŸ˜ƒ', 'ðŸ˜€', 'ðŸ˜', 'ðŸ˜„', 'ðŸ™‚', 'ðŸ˜†', 'ðŸ˜…', 'ðŸ˜Ž', 'ðŸ˜¸'];
    let selectedEmoji = null;
    for (const smile of knownSmileEmojis) {
      const emojiLocator = await page.$(`.emoji-item:text("${smile}")`);
      if (emojiLocator) {
        await emojiLocator.click();
        selectedEmoji = smile;
        break;
      }
    }
    // If not found, fallback to click the first emoji in the grid
    if (!selectedEmoji) {
      const firstEmoji = await page.$('.emoji-item');
      if (firstEmoji) {
        selectedEmoji = await firstEmoji.textContent();
        await firstEmoji.click();
      } else {
        throw new Error('No emoji found to select.');
      }
    }

    // Wait for the emoji picker to close (wait for absence of .emoji-item)
    await page.waitForSelector('.emoji-item', { state: 'detached', timeout: 2000 });

    // Step 4: Verify the emoji appears in the thread textarea
    // Get the textarea element
    const textarea = await page.waitForSelector('#thread-text', { timeout: 2000 });

    // Take screenshot of textarea after emoji is inserted
    const step3 = path.join(screenshotsDir, 'step_3_emoji_inserted.png');
    await page.screenshot({ path: step3 });
    screenshots.push(step3);

    // Extract textarea value
    const textareaValue = await textarea.evaluate(node => node.value);

    // Check if the selected emoji is present in the textarea
    const emojiFound = selectedEmoji && textareaValue.includes(selectedEmoji);

    return {
      success: emojiFound,
      action: emojiFound
        ? `Clicked emoji button, selected emoji "${selectedEmoji}", and verified it appears in the thread text`
        : 'Emoji not found in textarea after insertion',
      extractedData: {
        selectedEmoji,
        textareaValue,
        emojiFound
      },
      screenshots,
      error: emojiFound ? null : 'Emoji was not inserted into the textarea.'
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
