    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_inserted.png'));

    // Extract the thread text area value
    await page.waitForSelector('#thread-text', { state: 'visible', timeout: 2000 });
    const threadTextValue = await page.$eval('#thread-text', el => el.value);

    // Check if the smile emoji is present in the thread text
    let emojiFoundInText = false;
    let usedEmoji = foundEmoji;
    if (foundEmoji && threadTextValue.includes(foundEmoji)) {
      emojiFoundInText = true;
    } else {
      // Try to check if any smile emoji is present
      for (const smile of smileEmojis) {
        if (threadTextValue.includes(smile)) {
          emojiFoundInText = true;
          usedEmoji = smile;
          break;
        }
      }
    }

    return {
      success: emojiFoundInText,
      action: "Clicked emoji button, selected a smile emoji, and verified it appears in the thread text",
      extractedData: {
        threadText: threadTextValue,
        emojiInserted: usedEmoji,
        emojiFoundInText
      },
      screenshots,
      error: emojiFoundInText ? null : "Smile emoji was not found in the thread text"
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
