    await emojiButton[0].click();

    // Wait for the emoji picker popup/grid to appear
    await page.waitForSelector('.emoji-grid', { timeout: 4000 });

    // Find a smile emoji - prefer classic smileys, fallback to any emoji
    // We'll choose the first emoji that looks like a smile (common: ðŸ˜€ ðŸ˜ƒ ðŸ˜„ ðŸ˜ ðŸ˜Š ðŸ˜† ðŸ˜… ðŸ˜‚ ðŸ™‚ ðŸ™ƒ)
    // We'll select by visible text content
    const smileEmojis = ['ðŸ˜€','ðŸ˜ƒ','ðŸ˜„','ðŸ˜','ðŸ˜Š','ðŸ˜†','ðŸ˜…','ðŸ˜‚','ðŸ™‚','ðŸ™ƒ','ðŸ˜¸','ðŸ˜º','ðŸ˜»'];
    let foundEmoji = null;
    for (const smile of smileEmojis) {
      const emojiEl = await page.$(`.emoji-item:text("${smile}")`);
      if (emojiEl) {
        foundEmoji = { el: emojiEl, char: smile };
        break;
      }
