    // If none found, just select the first emoji
    if (!foundEmoji) {
      const firstEmojiEl = await page.$('.emoji-item');
      if (!firstEmojiEl) throw new Error('No emoji found in picker');
      const emojiChar = await firstEmojiEl.textContent();
      foundEmoji = { el: firstEmojiEl, char: emojiChar.trim() };
    }

    // Click the selected emoji
    await foundEmoji.el.click();

    // Wait for the emoji picker to disappear (optional: verify .emoji-grid is gone)
