    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_inserted.png'));

    // Extract the text in the textarea
    const threadText = await page.$eval('textarea#thread-text', el => el.value);

    // Verify the smile emoji is present
    const emojiPresent = threadText.includes(foundEmoji.char);

    return {
      success: emojiPresent,
      action: `Clicked emoji button, selected smile emoji (${foundEmoji.char}), verified it appears in the thread text`,
      extractedData: {
        insertedEmoji: foundEmoji.char,
        threadText,
        emojiPresent
      },
      screenshots,
      error: emojiPresent ? null : "Emoji was not inserted into textarea"
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

