import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the emoji add button (use aria-label or icon class as alternative selector)
    // The button has title="Add emoji" and id="add-emoji"
    const emojiBtnSelector = 'button[title="Add emoji"], #add-emoji, .tool-btn:has(i.fa-smile)';
    await page.waitForSelector(emojiBtnSelector, { timeout: 4000 });

    // Click the emoji button (try :has selector for robustness)
    const emojiBtn = await page.$(emojiBtnSelector);
    if (!emojiBtn) throw new Error("Emoji button not found");
    await emojiBtn.click();

    // Wait for the emoji picker grid (class .emoji-grid) to appear
    await page.waitForSelector('.emoji-grid', { timeout: 4000 });

    // Take screenshot after emoji picker is open
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_picker_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_picker_open.png'));

    // Find a smile emoji in the grid
    // Try to select a visible emoji-item that contains a smile emoji (Unicode: ðŸ˜„, ðŸ˜ƒ, ðŸ˜, ðŸ˜Š, ðŸ˜€, ðŸ˜†, ðŸ˜…, ðŸ˜‚, etc.)
    // We'll look for an emoji-item whose textContent matches a typical smile emoji
    const possibleSmileEmojis = ['ðŸ˜„', 'ðŸ˜ƒ', 'ðŸ˜', 'ðŸ˜Š', 'ðŸ˜€', 'ðŸ˜†', 'ðŸ˜…', 'ðŸ˜‚', 'ðŸ™‚', 'ðŸ˜º', 'ðŸ˜¸', 'ðŸ˜»'];
    let smileEmojiEl = null;
    for (const emoji of possibleSmileEmojis) {
      smileEmojiEl = await page.$(`.emoji-item:text("${emoji}")`);
      if (!smileEmojiEl) {
        // Try alt selector: find by evaluating all .emoji-item
        const handles = await page.$$('.emoji-item');
        for (const handle of handles) {
          const text = await handle.evaluate(el => el.textContent.trim());
          if (text === emoji) {
            smileEmojiEl = handle;
            break;
          }
        }
      }
      if (smileEmojiEl) break;
    }

    if (!smileEmojiEl) {
      // As fallback, pick the first emoji-item in the grid
      const allEmojiItems = await page.$$('.emoji-item');
      if (allEmojiItems.length === 0) throw new Error('No emoji items found in picker');
      smileEmojiEl = allEmojiItems[0];
    }

    // Get the actual emoji character for later verification
    const selectedEmoji = await smileEmojiEl.evaluate(el => el.textContent.trim());

    // Click the smile emoji
    await smileEmojiEl.click();

    // Wait a moment for emoji to appear in textarea
    await page.waitForTimeout(400);

    // Take screenshot after emoji is inserted
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_inserted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_inserted.png'));

    // Verify that the emoji is in the thread text area value
    const threadTextareaSelector = '#thread-text, textarea[placeholder="Start a thread..."]';
    await page.waitForSelector(threadTextareaSelector, { timeout: 2000 });

    const textareaValue = await page.$eval(threadTextareaSelector, el => el.value || el.textContent || '');

    // Check if the selected emoji is present in the textarea
    const emojiInText = textareaValue.includes(selectedEmoji);

    // Final screenshot for confirmation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_verification.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_verification.png'));

    return {
      success: emojiInText,
      action: `Clicked emoji button, selected emoji "${selectedEmoji}", and verified it appears in the thread text`,
      extractedData: {
        insertedEmoji: selectedEmoji,
        textareaContent: textareaValue,
        emojiPresent: emojiInText
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

