import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load
    await page.waitForSelector('.thread-item', { timeout: 10000 });

    // Take screenshot after threads load
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_threads_loaded.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_threads_loaded.png'));

    // Extract all thread content and metadata
    const threadsData = await page.$$eval('.thread-item', (threads) => {
      return threads.map(thread => {
        const text = thread.querySelector('.thread-text')?.textContent || '';
        const time = thread.querySelector('.thread-time')?.textContent || '';
        const actions = Array.from(thread.querySelectorAll('.thread-action span'))
          .map(span => span.textContent.trim())
          .filter(text => text.length > 0);

        return {
          text: text.trim(),
          time: time.trim(),
          actions: actions
        };
      });
    });

    // Take screenshot after data extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_data_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_data_extracted.png'));

    return {
      success: true,
      action: "Extracted all thread text content and metadata from the current feed",
      extractedData: {
        threads: threadsData,
        totalThreads: threadsData.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

