import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and focus on the thread text area
    await page.waitForSelector('#thread-text');
    const textArea = await page.$('#thread-text');
    await textArea.click();

    // Type the test text
    const testText = 'Testing character count functionality';
    await page.keyboard.type(testText);

    // Wait for character count to update
    await page.waitForFunction((expectedLength) => {
      const charCount = document.querySelector('#char-count');
      return charCount && charCount.textContent.includes(expectedLength);
    }, 35); // 35 characters in the test text

    // Take screenshot after typing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_typing.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_typing.png'));

    // Extract character count data
    const charCountText = await page.$eval('#char-count', el => el.textContent);
    const currentCount = parseInt(charCountText.split('/')[0]);

    return {
      success: true,
      action: "Typed 'Testing character count functionality' and verified character count updates",
      extractedData: {
        expectedCharacterCount: 35,
        actualCharacterCount: currentCount,
        characterCountDisplay: charCountText
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the thread textarea to be visible
    await page.waitForSelector('#thread-text', { state: 'visible' });

    // Clear any existing text and type the test string
    const testText = 'Testing character count functionality';
    await page.fill('#thread-text', '');
    await page.type('#thread-text', testText, { delay: 50 });

    // Wait for character count to update
    await page.waitForFunction((expectedText) => {
      const countElement = document.querySelector('#char-count');
      return countElement && countElement.textContent === `${expectedText.length}/500`;
    }, testText.length);

    // Verify character count updated correctly
    const charCount = await page.textContent('#char-count');
    const expectedCount = `${testText.length}/500`;

    if (charCount !== expectedCount) {
      throw new Error(`Character count mismatch. Expected: ${expectedCount}, Got: ${charCount}`);
    }

    // Take screenshot after typing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_typing.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_typing.png'));

    return {
      success: true,
      action: "Typed 'Testing character count functionality' and verified character count updates to 35/500",
      extractedData: {
        textEntered: testText,
        characterCount: charCount,
        expectedCount: expectedCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

