import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click settings button
    await page.waitForSelector('#settings-btn');
    await page.click('#settings-btn');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_settings_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_settings_opened.png'));

    // Click Appearance tab
    await page.waitForSelector('.tab-btn[data-tab="appearance"]');
    await page.click('.tab-btn[data-tab="appearance"]');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_appearance_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_appearance_tab.png'));

    // Change theme to Light
    await page.waitForSelector('#theme-select');
    await page.selectOption('#theme-select', 'light');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_theme_changed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_theme_changed.png'));

    // Verify theme was changed
    const selectedTheme = await page.$eval('#theme-select', select => select.value);
    const isLightTheme = await page.$eval('body', body => body.classList.contains('light-theme'));

    return {
      success: true,
      action: "Opened settings, navigated to Appearance tab, and changed theme to Light",
      extractedData: {
        selectedTheme,
        isLightThemeApplied: isLightTheme
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click settings button using multiple selector strategies
    const settingsBtn = await page.waitForSelector('#settings-btn', { state: 'visible' });
    await settingsBtn.click();
    await page.waitForTimeout(1000); // Wait for settings to open

    // Wait for settings modal and click Appearance tab
    await page.waitForSelector('.tab-btn[data-tab="appearance"]', { state: 'visible' });
    const appearanceTab = await page.$('.tab-btn[data-tab="appearance"]');
    await appearanceTab.click();
    await page.waitForTimeout(500); // Wait for tab switch

    // Select Light theme from dropdown
    await page.waitForSelector('#theme-select', { state: 'visible' });
    await page.selectOption('#theme-select', 'light');
    await page.waitForTimeout(1000); // Wait for theme change

    // Verify theme change by checking body class
    const bodyClass = await page.$eval('body', el => el.className);
    const isLightTheme = bodyClass.includes('light-theme');

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_theme_changed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_theme_changed.png'));

    return {
      success: true,
      action: "Opened settings, switched to Appearance tab, and changed theme to Light",
      extractedData: {
        theme: 'light',
        bodyClass: bodyClass,
        themeChanged: isLightTheme
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete theme change task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

