import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the left sidebar to load
    await page.waitForSelector('.sidebar', { timeout: 5000 });

    // Wait for and click the Settings button using ID selector
    await page.waitForSelector('#settings-btn', { timeout: 5000 });
    await page.click('#settings-btn');

    // Wait for settings modal to appear (checking for settings input fields)
    await page.waitForSelector('#settings-username', { timeout: 5000 });

    // Take screenshot after opening settings modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_settings_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_settings_modal_open.png'));

    // Extract settings modal data to verify task completion
    const extractedData = {
      modalVisible: await page.$eval('#settings-username', el => el.offsetParent !== null),
      usernamePlaceholder: await page.$eval('#settings-username', el => el.placeholder),
      displayNamePlaceholder: await page.$eval('#settings-display-name', el => el.placeholder)
    };

    return {
      success: true,
      action: "Clicked Settings button in left sidebar and opened settings modal",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

