import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the left sidebar to be visible
    await page.waitForSelector('.sidebar', { state: 'visible' });

    // Find the Settings button using multiple selector strategies
    const settingsButton = await page.$('#settings-btn');
    if (!settingsButton) {
      throw new Error('Settings button not found');
    }

    // Click the Settings button
    await settingsButton.click();

    // Wait for settings modal to appear - checking for a unique element in the modal
    await page.waitForSelector('#settings-username', { state: 'visible', timeout: 5000 });

    // Take screenshot after opening settings modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_settings_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_settings_modal_open.png'));

    // Extract some data from the settings modal to verify it's open
    const usernameInput = await page.$('#settings-username');
    const usernameValue = await usernameInput.getAttribute('value');

    return {
      success: true,
      action: "Clicked Settings button in left sidebar and opened settings modal",
      extractedData: {
        username: usernameValue,
        modalOpen: true
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to open settings modal",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
