import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click emoji button
    await page.waitForSelector('#add-emoji');
    await page.click('#add-emoji');

    // Take screenshot after opening emoji picker
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_picker_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_picker_open.png'));

    // Wait for emoji items to load and select first smile emoji
    await page.waitForSelector('.emoji-item');
    const firstEmoji = await page.$('.emoji-item');
    const emojiText = await firstEmoji.textContent();
    await firstEmoji.click();

    // Take screenshot after selecting emoji
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_selected.png'));

    // Verify emoji appears in thread text
    await page.waitForSelector('#thread-text');
    const threadText = await page.$eval('#thread-text', el => el.value);

    const verificationResult = threadText.includes(emojiText);

    return {
      success: true,
      action: "Clicked emoji button, selected smile emoji, and verified it appears in thread text",
      extractedData: {
        selectedEmoji: emojiText,
        threadTextContent: threadText,
        emojiAdded: verificationResult
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

