import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click emoji button using multiple selector strategies
    const emojiButton = await page.waitForSelector('#add-emoji, button#add-emoji, [id="add-emoji"]', { state: 'visible' });
    await emojiButton.click();
    await page.waitForTimeout(1000); // Brief pause for emoji picker animation

    // Wait for emoji picker to appear and take screenshot
    await page.waitForSelector('.emoji-grid, .emoji-item', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_picker.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_picker.png'));

    // Find and click a smile emoji using multiple strategies
    const emojiItems = await page.$$('.emoji-item');
    let smileEmoji = null;

    // Try to find a smile emoji by checking text content
    for (const emoji of emojiItems) {
      const emojiText = await emoji.textContent();
      if (emojiText && ['ðŸ˜Š', 'ðŸ˜€', 'ðŸ˜‚', 'ðŸ˜', 'ðŸ¥°'].includes(emojiText.trim())) {
        smileEmoji = emoji;
        break;
      }
    }

    // If no specific smile emoji found, use first available emoji
    if (!smileEmoji && emojiItems.length > 0) {
      smileEmoji = emojiItems[0];
    }

    if (smileEmoji) {
      const emojiText = await smileEmoji.textContent();
      await smileEmoji.click();

      // Verify emoji appears in thread text
      await page.waitForFunction((expectedEmoji) => {
        const textarea = document.querySelector('#thread-text');
        return textarea && textarea.value.includes(expectedEmoji);
      }, emojiText.trim());

      // Take final verification screenshot
      await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_added.png') });
      screenshots.push(path.join(screenshotsDir, 'step_3_emoji_added.png'));

      // Extract verification data
      const threadText = await page.$eval('#thread-text', el => el.value);
      const extractedData = {
        emojiAdded: emojiText.trim(),
        threadText: threadText
      };

