import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on Search navigation item using data-view attribute
    await page.waitForSelector('[data-view="search"]');
    await page.click('[data-view="search"]');
    await page.waitForTimeout(1000);

    // Wait for search view to become active and search input to be visible
    await page.waitForSelector('#search-view.active');
    await page.waitForSelector('#search-input');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_view.png'));

    // Type 'jun' in the search input
    await page.fill('#search-input', 'jun');
    await page.waitForTimeout(500);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_typed_jun.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_typed_jun.png'));

    // Switch to People tab using CSS selector with data-tab attribute
    await page.waitForSelector('.tab-btn[data-tab="people"]');
    await page.click('.tab-btn[data-tab="people"]');
    await page.waitForTimeout(1000);

    // Verify People tab is active and take final screenshot
    await page.waitForSelector('.tab-btn[data-tab="people"].active');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_people_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_people_tab.png'));

    // Extract search results data to verify completion
    const searchResults = await page.$$eval('.search-result-item', items => 
      items.map(item => ({
        username: item.querySelector('.username')?.textContent || '',
        handle: item.querySelector('.user-handle')?.textContent || ''
      }))
    );

    return {
      success: true,
      action: "Navigated to search view, typed 'jun' in search input, and switched to People tab",
      extractedData: {
        searchQuery: 'jun',
        activeTab: 'people',
        resultsCount: searchResults.length,
        results: searchResults
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
