import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load and find the first reply button
    await page.waitForSelector('.thread-action[data-action="reply"]', { timeout: 5000 });
    const replyButtons = await page.$$('.thread-action[data-action="reply"]');

    if (replyButtons.length === 0) {
      throw new Error('No reply buttons found');
    }

    // Perform 10 rapid open/close cycles
    for (let i = 0; i < 10; i++) {
      // Click reply button
      await replyButtons[0].click();

      // Wait for modal to appear
      await page.waitForSelector('#close-reply-modal', { state: 'visible', timeout: 2000 });

      // Click close button
      await page.click('#close-reply-modal');

      // Wait for modal to disappear
      await page.waitForSelector('#close-reply-modal', { state: 'hidden', timeout: 2000 });
    }

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_final.png'));

    return {
      success: true,
      action: "Successfully opened and closed reply modal 10 times in rapid succession",
      extractedData: {
        cyclesCompleted: 10,
        modalSelector: '#close-reply-modal',
        replyButtonSelector: '.thread-action[data-action="reply"]'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

