import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load
    await page.waitForSelector('#threads-feed');

    // Perform 10 open/close cycles of reply modal
    for (let i = 0; i < 10; i++) {
      // Wait for and click reply button using multiple selector strategies
      const replyButton = await page.waitForSelector('.thread-action[data-action="reply"]', { timeout: 5000 });
      await replyButton.click();

      // Wait for modal to open
      await page.waitForSelector('#close-reply-modal', { state: 'visible', timeout: 5000 });

      // Take screenshot of open modal on first iteration
      if (i === 0) {
        await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
        screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));
      }

      // Close the modal
      const closeButton = await page.waitForSelector('#close-reply-modal', { timeout: 5000 });
      await closeButton.click();

      // Wait for modal to close
      await page.waitForSelector('#close-reply-modal', { state: 'hidden', timeout: 5000 });

      // Brief pause between iterations
      await page.waitForTimeout(100);
    }

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_completed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_completed.png'));

    return {
      success: true,
      action: "Successfully opened and closed reply modal 10 times in succession",
      extractedData: { cyclesCompleted: 10 },
      screenshots,
      error: null
    };
  } catch (error) {
    // Capture error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
