import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on Search navigation item to open search view
    await page.waitForSelector('.nav-item[data-view="search"]');
    await page.click('.nav-item[data-view="search"]');

    // Wait for search view to load and take screenshot
    await page.waitForSelector('#search-view.active');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_view.png'));

    // Type 'jun' in search input
    await page.waitForSelector('#search-input');
    await page.fill('#search-input', 'jun');

    // Wait for search results to load and take screenshot
    await page.waitForTimeout(500); // Wait for potential search results to appear
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_jun.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_jun.png'));

    // Switch to People tab
    await page.waitForSelector('.tab-btn[data-tab="people"]');
    await page.click('.tab-btn[data-tab="people"]');

    // Wait for People tab to activate and take screenshot
    await page.waitForSelector('.tab-btn[data-tab="people"].active');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_people_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_people_tab.png'));

    // Extract search results data
    const extractedData = {
      searchTerm: 'jun',
      activeTab: 'people',
      searchResults: []
    };

    // Try to extract people results if available
    const peopleResults = await page.$$eval('.user-result, [data-user]', elements => 
      elements.map(el => ({
        username: el.querySelector('.username')?.textContent || '',
        handle: el.querySelector('.user-handle')?.textContent || ''
      }))
    );

    if (peopleResults.length > 0) {
      extractedData.searchResults = peopleResults;
    }

    return {
      success: true,
      action: "Navigated to search view, typed 'jun' in search input, and switched to People tab",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete search and tab switching task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

