import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on Search navigation item
    await page.waitForSelector('[data-view="search"]');
    await page.click('[data-view="search"]');

    // Wait for search view to be active
    await page.waitForSelector('#search-view.active');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_view.png'));

    // Type 'jun' in search input
    await page.waitForSelector('#search-input');
    await page.fill('#search-input', 'jun');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_input.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_input.png'));

    // Switch to People tab
    await page.waitForSelector('button.tab-btn[data-tab="people"]');
    await page.click('button.tab-btn[data-tab="people"]');

    // Wait for tab to activate
    await page.waitForTimeout(500); // Small delay for UI update
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_people_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_people_tab.png'));

    // Verify the tab is active by checking if it has active class
    const isPeopleTabActive = await page.$eval(
      'button.tab-btn[data-tab="people"]',
      (el) => el.classList.contains('active')
    );

    return {
      success: true,
      action: "Navigated to search view, typed 'jun' in search input, and switched to People tab",
      extractedData: {
        searchTerm: 'jun',
        activeTab: 'people',
        isPeopleTabActive: isPeopleTabActive
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
