import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for thread feed to load
    await page.waitForSelector('#threads-feed', { timeout: 5000 });

    // Wait for username elements to be present
    await page.waitForSelector('.thread-username', { timeout: 10000 });

    // Extract all visible usernames from the thread feed
    const usernames = await page.$$eval('.thread-username', elements => 
      elements.map(el => el.textContent.trim())
    );

    // Take screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_usernames_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_usernames_extracted.png'));

    return {
      success: true,
      action: "Collected all visible usernames from the thread feed",
      extractedData: { usernames },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for thread feed to load
    await page.waitForSelector('#threads-feed', { timeout: 5000 });

    // Extract usernames using multiple selector strategies
    const usernames = await page.$$eval('.thread-item .thread-username', elements => 
      elements.map(el => el.textContent.trim())
    );

    // Alternative selector strategy if primary fails
    let extractedUsernames = usernames;
    if (usernames.length === 0) {
      extractedUsernames = await page.$$eval('[data-user]', elements => 
        elements.map(el => el.dataset.user)
      );
    }

    // Take screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_usernames_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_usernames_extracted.png'));

    return {
      success: true,
      action: "Collected all visible usernames from thread feed",
      extractedData: {
        usernames: extractedUsernames,
        count: extractedUsernames.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error_state.png') });
    screenshots.push(path.join(screenshotsDir, 'error_state.png'));

    return {
      success: false,
      action: "Failed to collect usernames from thread feed",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

